import { getOpenAITextResponse } from './chat-service';
import { AIGenerationRequest, AIGeneratedContent } from '../types/training';

/**
 * AI Training Content Generator
 * Uses OpenAI GPT-4o to generate training content, lessons, and quizzes
 */

export const generateTrainingContent = async (request: AIGenerationRequest): Promise<AIGeneratedContent> => {
  const prompt = `
Du bist ein Experte f√ºr Unternehmensschulungen und E-Learning. Erstelle eine strukturierte Schulung basierend auf folgenden Vorgaben:

**Schulungsanfrage:**
- Thema: ${request.topic}
- Beschreibung: ${request.description}
- Zielgruppe: ${request.targetAudience}
- Dauer: ${request.duration} Minuten
- Schwierigkeitsgrad: ${request.difficulty}
- Anzahl Lektionen: ${request.lessonCount}
- Quiz einschlie√üen: ${request.includeQuiz ? 'Ja' : 'Nein'}

**Aufgabe:**
Erstelle eine vollst√§ndige Schulung mit folgender Struktur:

1. **Schulungstitel**: Pr√§gnanter, professioneller Titel
2. **Beschreibung**: Umfassende Beschreibung der Schulung (2-3 S√§tze)
3. **Lektionen**: ${request.lessonCount} Lektionen mit jeweils:
   - Titel der Lektion
   - Kurze Beschreibung (1-2 S√§tze)
   - Detaillierter Inhalt (strukturiert mit √úberschriften, Aufz√§hlungen, praktischen Beispielen)
   ${request.includeQuiz ? '- Quiz mit 2-3 Multiple-Choice-Fragen pro Lektion\n   - Jede Frage mit 4 Antwortm√∂glichkeiten, korrekter Antwort und Erkl√§rung' : ''}

${request.includeQuiz ? `4. **Abschlusspr√ºfung**: 
   - 2-3 zusammenfassende Fragen √ºber alle Lektionen
   - Bestehensgrenze: 70%` : ''}

**Wichtige Anforderungen:**
- Inhalt muss praxisnah und unternehmensrelevant sein
- Verwende klare, verst√§ndliche Sprache
- Strukturiere den Inhalt logisch und aufbauend
- Beispiele und Fallstudien einbauen
- Bei ${request.difficulty === 'BEGINNER' ? 'Anf√§nger: Grundlagen erkl√§ren' : request.difficulty === 'INTERMEDIATE' ? 'Fortgeschritten: Vertiefte Kenntnisse vermitteln' : 'Experte: Spezialisiertes Fachwissen'}

**Antwortformat:**
Antworte ausschlie√ülich in folgendem JSON-Format:

{
  "title": "Schulungstitel",
  "description": "Schulungsbeschreibung",
  "lessons": [
    {
      "title": "Lektionstitel",
      "description": "Lektionsbeschreibung",
      "content": "Detaillierter Lektionsinhalt mit Markdown-Formatierung",
      ${request.includeQuiz ? `"quiz": {
        "questions": [
          {
            "question": "Frage?",
            "options": ["Option A", "Option B", "Option C", "Option D"],
            "correctAnswer": 0,
            "explanation": "Erkl√§rung der korrekten Antwort"
          }
        ],
        "passingScore": 80
      }` : ''}
    }
  ]${request.includeQuiz ? `,
  "finalQuiz": {
    "questions": [
      {
        "question": "Abschlussfrage?",
        "options": ["Option A", "Option B", "Option C", "Option D"],
        "correctAnswer": 0,
        "explanation": "Erkl√§rung"
      }
    ],
    "passingScore": 70
  }` : ''}
}

Erstelle jetzt die Schulung:`;

  try {
    const aiResponse = await getOpenAITextResponse([
      {
        role: 'system',
        content: 'Du bist ein Experte f√ºr Unternehmensschulungen und E-Learning-Content. Erstelle hochwertige, strukturierte Schulungsinhalte in deutscher Sprache.'
      },
      {
        role: 'user',
        content: prompt
      }
    ], { model: 'gpt-4o' });

    const response = aiResponse.content;

    // Parse the AI response
    let parsedContent: AIGeneratedContent;

    try {
      // Extract JSON from response (in case there's additional text)
      const jsonMatch = response.match(/\{[\s\S]*\}/);
      const jsonString = jsonMatch ? jsonMatch[0] : response;
      parsedContent = JSON.parse(jsonString);
    } catch (parseError) {
      // Fallback: create structured content from text response
      parsedContent = {
        title: `${request.topic} - Umfassende Schulung`,
        description: `Eine strukturierte Schulung zu ${request.topic} f√ºr ${request.targetAudience}. Diese Schulung vermittelt alle wichtigen Grundlagen und praktischen Anwendungen.`,
        lessons: Array.from({ length: request.lessonCount }, (_, i) => ({
          title: `Lektion ${i + 1}: ${request.topic} Grundlagen`,
          description: `In dieser Lektion lernen Sie die wichtigsten Aspekte von ${request.topic} kennen.`,
          content: `# Lektion ${i + 1}: ${request.topic}\n\n${response.substring(0, 500)}...`,
          quiz: request.includeQuiz ? {
            questions: [
              {
                question: `Was ist das wichtigste Prinzip bei ${request.topic}?`,
                options: [
                  'Sicherheit und Compliance',
                  'Effizienz',
                  'Kostenoptimierung',
                  'Alle genannten Punkte'
                ],
                correctAnswer: 3,
                explanation: `Bei ${request.topic} sind alle Aspekte wichtig, aber besonders die Balance zwischen Sicherheit, Effizienz und Kosten.`
              }
            ],
            passingScore: 80
          } : undefined
        })),
        finalQuiz: request.includeQuiz ? {
          questions: [
            {
              question: `Was haben Sie in dieser ${request.topic} Schulung gelernt?`,
              options: [
                'Grundlagen und Best Practices',
                'Nur theoretisches Wissen',
                'Veraltete Methoden',
                'Nichts Neues'
              ],
              correctAnswer: 0,
              explanation: 'Diese Schulung vermittelt sowohl theoretische Grundlagen als auch praktische Best Practices.'
            }
          ],
          passingScore: 70
        } : undefined
      };
    }

    // Validate the generated content
    if (!parsedContent.title || !parsedContent.description || !parsedContent.lessons) {
      throw new Error('Invalid AI response structure');
    }

    return parsedContent;
  } catch (error) {
    console.error('AI Training Generation Error:', error);
    throw new Error('Fehler bei der KI-Generierung. Bitte versuchen Sie es erneut.');
  }
};

/**
 * Generate a training certificate using AI
 */
export const generateCertificateText = async (
  trainingTitle: string, 
  userName: string, 
  completionDate: string,
  score: number
): Promise<string> => {
  const prompt = `
Erstelle einen professionellen Zertifikatstext f√ºr eine abgeschlossene Unternehmensschulung.

**Details:**
- Schulung: ${trainingTitle}
- Teilnehmer: ${userName}
- Abschlussdatum: ${completionDate}
- Ergebnis: ${score}%

**Anforderungen:**
- Professioneller, offizieller Ton
- Deutsche Sprache
- Gratulation zur erfolgreichen Teilnahme
- Best√§tigung der erworbenen Kenntnisse
- Verwende "HRthis GmbH" als ausstellendes Unternehmen

Erstelle einen strukturierten Zertifikatstext:`;

  try {
    const aiResponse = await getOpenAITextResponse([
      {
        role: 'system',
        content: 'Du erstellst professionelle Zertifikatstexte f√ºr Unternehmensschulungen in deutscher Sprache.'
      },
      {
        role: 'user',
        content: prompt
      }
    ], { model: 'gpt-4o' });

    return aiResponse.content;
  } catch (error) {
    console.error('Certificate Generation Error:', error);

    // Fallback certificate text
    return `
ZERTIFIKAT

Hiermit wird best√§tigt, dass

${userName}

die Schulung "${trainingTitle}" erfolgreich abgeschlossen hat.

Abschlussdatum: ${completionDate}
Ergebnis: ${score}%

Diese Schulung vermittelte wichtige Kenntnisse und F√§higkeiten f√ºr den beruflichen Alltag.

HRthis GmbH
Personalabteilung
    `.trim();
  }
};

/**
 * Generate email content for training notifications
 */
export const generateTrainingNotificationEmail = async (
  type: 'NEW_TRAINING' | 'REMINDER' | 'DEADLINE_APPROACHING' | 'COMPLETED' | 'FAILED',
  userName: string,
  trainingTitle: string,
  additionalInfo?: string
): Promise<{ subject: string; body: string }> => {
  let promptContext = '';

  switch (type) {
    case 'NEW_TRAINING':
      promptContext = 'eine neue Schulung ist verf√ºgbar';
      break;
    case 'REMINDER':
      promptContext = 'Erinnerung an eine nicht abgeschlossene Schulung';
      break;
    case 'DEADLINE_APPROACHING':
      promptContext = 'die Deadline einer Schulung n√§hert sich';
      break;
    case 'COMPLETED':
      promptContext = 'Gratulation zur abgeschlossenen Schulung';
      break;
    case 'FAILED':
      promptContext = 'Information √ºber nicht bestandene Schulung';
      break;
  }

  const prompt = `
Erstelle eine professionelle E-Mail f√ºr ${promptContext}.

**Details:**
- Empf√§nger: ${userName}
- Schulung: ${trainingTitle}
- Zus√§tzliche Info: ${additionalInfo || 'Keine'}
- Absender: HRthis GmbH Personalabteilung

**Anforderungen:**
- Professioneller aber freundlicher Ton
- Deutsche Sprache
- Passende Betreffzeile
- Strukturierte E-Mail mit klaren Handlungsaufforderungen
- Link zur HRthis App erw√§hnen

Erstelle Betreff und E-Mail-Text:`;

  try {
    const aiResponse = await getOpenAITextResponse([
      {
        role: 'system',
        content: 'Du erstellst professionelle E-Mail-Benachrichtigungen f√ºr Unternehmensschulungen in deutscher Sprache.'
      },
      {
        role: 'user',
        content: prompt
      }
    ], { model: 'gpt-4o' });

    const response = aiResponse.content;

    // Extract subject and body from response
    const lines = response.split('\n');
    const subjectLine = lines.find((line: string) => line.toLowerCase().includes('betreff:') || line.toLowerCase().includes('subject:'));
    const subject = subjectLine ? subjectLine.replace(/betreff:|subject:/i, '').trim() : `HRthis: ${trainingTitle}`;

    const bodyStart = response.indexOf('\n\n') > 0 ? response.indexOf('\n\n') + 2 : 0;
    const body = response.substring(bodyStart).trim();

    return { subject, body };
  } catch (error) {
    console.error('Email Generation Error:', error);

    // Fallback email content
    const fallbackSubjects = {
      NEW_TRAINING: `Neue Schulung verf√ºgbar: ${trainingTitle}`,
      REMINDER: `Erinnerung: Schulung "${trainingTitle}" noch nicht abgeschlossen`,
      DEADLINE_APPROACHING: `Deadline n√§hert sich: ${trainingTitle}`,
      COMPLETED: `Gl√ºckwunsch! Schulung "${trainingTitle}" erfolgreich abgeschlossen`,
      FAILED: `Schulung "${trainingTitle}" - Wiederholung erforderlich`
    };

    return {
      subject: fallbackSubjects[type],
      body: `Hallo ${userName},\n\nbez√ºglich der Schulung "${trainingTitle}" gibt es eine wichtige Information.\n\nBitte loggen Sie sich in die HRthis App ein, um weitere Details zu erfahren.\n\nBeste Gr√º√üe\nIhr HRthis Team`
    };
  }
};

/*
IMPORTANT NOTICE: DO NOT REMOVE
This is a custom audio transcription service that uses a custom API endpoint maintained by Vibecode.
You can use this function to transcribe audio files, and it will return the text of the audio file.
*/

/**
 * Transcribe an audio file
 * @param localAudioUri - The local URI of the audio file to transcribe. Obtained via the expo-av library.
 * @returns The text of the audio file
 */
export const transcribeAudio = async (localAudioUri: string) => {
  try {
    // Create FormData for the audio file
    const formData = new FormData();
    formData.append("file", {
      uri: localAudioUri,
      type: "audio/m4a",
      name: "recording.m4a",
    } as any);
    formData.append("model", "gpt-4o-transcribe");
    formData.append("language", "en");

    const OPENAI_API_KEY = process.env.EXPO_PUBLIC_VIBECODE_OPENAI_API_KEY;
    if (!OPENAI_API_KEY) {
      throw new Error("OPENAI_API_KEY is not set");
    }

    // API call to OpenAI's gpt-4o-transcribe
    const response = await fetch("https://api.openai.com/v1/audio/transcriptions", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${OPENAI_API_KEY}`,
      },
      body: formData,
    });

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(`Transcription failed: ${errorText}`);
    }

    const result = await response.json();
    return result.text;
  } catch (error) {
    console.error("Transcription error:", error);
    throw error;
  }
};

import React, { useState } from 'react';
import { useAchievementsStore } from '../state/achievements';
import { Achievement } from '../types/gamification';
import { cn } from '../utils/cn';
import { format } from 'date-fns';
import { de } from 'date-fns/locale';

interface AchievementsGalleryProps {
  userId: string;
  showLocked?: boolean;
  showProgress?: boolean;
  layout?: 'grid' | 'list';
  filterCategory?: string;
  className?: string;
}

interface AchievementCardProps {
  achievement: Achievement;
  isUnlocked: boolean;
  unlockedAt?: string;
  progress?: number;
  layout: 'grid' | 'list';
  onClick?: () => void;
}

const AchievementCard: React.FC<AchievementCardProps> = ({
  achievement,
  isUnlocked,
  unlockedAt,
  progress = 0,
  layout,
  onClick
}) => {
  const rarityColors = {
    common: 'from-gray-300 to-gray-500',
    rare: 'from-blue-300 to-blue-500',
    epic: 'from-purple-300 to-purple-500',
    legendary: 'from-yellow-300 to-orange-500'
  };

  const rarityLabels = {
    common: 'H√§ufig',
    rare: 'Selten', 
    epic: 'Episch',
    legendary: 'Legend√§r'
  };

  if (layout === 'list') {
    return (
      <div 
        className={cn(
          "flex items-center gap-4 p-4 bg-white rounded-lg border transition-all duration-200",
          isUnlocked 
            ? "border-gray-200 hover:shadow-md cursor-pointer" 
            : "border-gray-100 opacity-60",
          onClick && "hover:border-gray-300"
        )}
        onClick={onClick}
      >
        {/* Icon */}
        <div className={cn(
          "w-16 h-16 rounded-full flex items-center justify-center text-2xl text-white shadow-md relative",
          `bg-gradient-to-br ${rarityColors[achievement.rarity]}`,
          !isUnlocked && "grayscale"
        )}>
          <span>{achievement.icon}</span>
          {!isUnlocked && progress > 0 && (
            <div className="absolute inset-0 rounded-full border-4 border-gray-300">
              <div 
                className="absolute inset-0 rounded-full border-4 border-blue-500"
                style={{
                  clipPath: `polygon(50% 50%, 50% 0%, ${50 + (progress / 100) * 50}% 0%, 100% 100%, 0% 100%)`
                }}
              />
            </div>
          )}
        </div>

        {/* Content */}
        <div className="flex-1">
          <div className="flex items-start justify-between mb-2">
            <div>
              <h3 className={cn(
                "font-semibold",
                isUnlocked ? "text-gray-900" : "text-gray-500"
              )}>
                {achievement.name}
              </h3>
              <p className={cn(
                "text-sm",
                isUnlocked ? "text-gray-600" : "text-gray-400"
              )}>
                {achievement.description}
              </p>
            </div>

            <div className="text-right">
              <div className={cn(
                "text-xs px-2 py-1 rounded-full font-medium",
                achievement.rarity === 'legendary' ? 'bg-yellow-100 text-yellow-800' :
                achievement.rarity === 'epic' ? 'bg-purple-100 text-purple-800' :
                achievement.rarity === 'rare' ? 'bg-blue-100 text-blue-800' :
                'bg-gray-100 text-gray-800'
              )}>
                {rarityLabels[achievement.rarity]}
              </div>
              {isUnlocked && unlockedAt && (
                <p className="text-xs text-gray-500 mt-1">
                  {format(new Date(unlockedAt), 'dd.MM.yyyy', { locale: de })}
                </p>
              )}
            </div>
          </div>

          {/* Progress bar for locked achievements */}
          {!isUnlocked && progress > 0 && (
            <div className="mt-2">
              <div className="flex justify-between text-xs text-gray-500 mb-1">
                <span>Fortschritt</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${Math.min(100, progress)}%` }}
                />
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }

  // Grid layout
  return (
    <div 
      className={cn(
        "bg-white rounded-xl p-6 border transition-all duration-200 group",
        isUnlocked 
          ? "border-gray-200 hover:shadow-lg cursor-pointer hover:scale-105" 
          : "border-gray-100 opacity-70",
        onClick && "hover:border-gray-300"
      )}
      onClick={onClick}
    >
      {/* Icon */}
      <div className="relative mb-4">
        <div className={cn(
          "w-20 h-20 rounded-full flex items-center justify-center text-3xl text-white shadow-lg mx-auto",
          `bg-gradient-to-br ${rarityColors[achievement.rarity]}`,
          !isUnlocked && "grayscale"
        )}>
          <span>{achievement.icon}</span>
        </div>

        {/* Progress ring for locked achievements */}
        {!isUnlocked && progress > 0 && (
          <div className="absolute inset-0 flex items-center justify-center">
            <svg className="w-24 h-24 transform -rotate-90">
              <circle
                cx="48"
                cy="48" 
                r="44"
                stroke="currentColor"
                strokeWidth="4"
                fill="none"
                className="text-gray-200"
              />
              <circle
                cx="48"
                cy="48"
                r="44"
                stroke="currentColor"
                strokeWidth="4"
                fill="none"
                strokeDasharray={`${2 * Math.PI * 44}`}
                strokeDashoffset={`${2 * Math.PI * 44 * (1 - progress / 100)}`}
                className="text-blue-500 transition-all duration-300"
              />
            </svg>
          </div>
        )}

        {/* Lock icon for locked achievements */}
        {!isUnlocked && progress === 0 && (
          <div className="absolute bottom-0 right-0 w-8 h-8 bg-gray-400 rounded-full flex items-center justify-center text-white text-sm">
            üîí
          </div>
        )}
      </div>

      {/* Content */}
      <div className="text-center">
        <h3 className={cn(
          "font-semibold mb-2",
          isUnlocked ? "text-gray-900" : "text-gray-500"
        )}>
          {achievement.name}
        </h3>

        <p className={cn(
          "text-sm mb-3",
          isUnlocked ? "text-gray-600" : "text-gray-400"
        )}>
          {achievement.description}
        </p>

        {/* Rarity badge */}
        <div className={cn(
          "inline-block text-xs px-3 py-1 rounded-full font-medium mb-2",
          achievement.rarity === 'legendary' ? 'bg-yellow-100 text-yellow-800' :
          achievement.rarity === 'epic' ? 'bg-purple-100 text-purple-800' :
          achievement.rarity === 'rare' ? 'bg-blue-100 text-blue-800' :
          'bg-gray-100 text-gray-800'
        )}>
          {rarityLabels[achievement.rarity]}
        </div>

        {/* Unlock date */}
        {isUnlocked && unlockedAt && (
          <p className="text-xs text-gray-500">
            Freigeschaltet am {format(new Date(unlockedAt), 'dd.MM.yyyy', { locale: de })}
          </p>
        )}

        {/* Progress for locked achievements */}
        {!isUnlocked && progress > 0 && (
          <div className="mt-3">
            <p className="text-xs text-gray-500 mb-1">
              {Math.round(progress)}% abgeschlossen
            </p>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                style={{ width: `${Math.min(100, progress)}%` }}
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

