import React, { useState, useEffect } from 'react';
import { TestQuestion, TestAnswer, TestResult } from '../types/learning';
import { useLearningStore } from '../state/learning';
import { useAuthStore } from '../state/auth';
import { cn } from '../utils/cn';

interface QuizProps {
  videoId: string;
  questions: TestQuestion[];
  onComplete: (result: TestResult) => void;
}

export const Quiz: React.FC<QuizProps> = ({ videoId, questions, onComplete }) => {
  const { user } = useAuthStore();
  const { submitTestResult } = useLearningStore();

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState<TestAnswer[]>([]);
  const [selectedAnswer, setSelectedAnswer] = useState<string | string[]>('');
  const [showExplanation, setShowExplanation] = useState(false);
  const [isAnswered, setIsAnswered] = useState(false);
  const [startTime, setStartTime] = useState(Date.now());
  const [questionStartTime, setQuestionStartTime] = useState(Date.now());
  const [draggedItems, setDraggedItems] = useState<string[]>([]);
  const [dropZones, setDropZones] = useState<string[]>([]);

  const currentQuestion = questions[currentQuestionIndex];
  const isLastQuestion = currentQuestionIndex === questions.length - 1;

  useEffect(() => {
    setQuestionStartTime(Date.now());
    setSelectedAnswer('');
    setIsAnswered(false);
    setShowExplanation(false);

    // Initialize drag & drop for sorting questions
    if (currentQuestion.type === 'sorting' && currentQuestion.options) {
      setDraggedItems([...currentQuestion.options].sort(() => Math.random() - 0.5));
      setDropZones([]);
    }
  }, [currentQuestionIndex, currentQuestion]);

  const handleAnswer = () => {
    if (!user || isAnswered) return;

    const timeSpent = (Date.now() - questionStartTime) / 1000;
    const isCorrect = checkAnswer();

    const answer: TestAnswer = {
      questionId: currentQuestion.id,
      userAnswer: selectedAnswer,
      correct: isCorrect,
      timeSpent
    };

    setAnswers([...answers, answer]);
    setIsAnswered(true);
    setShowExplanation(true);
  };

  const checkAnswer = (): boolean => {
    switch (currentQuestion.type) {
      case 'multiple-choice':
        return selectedAnswer === currentQuestion.correctAnswer;

      case 'sorting':
        const correctOrder = currentQuestion.correctAnswer as string[];
        return JSON.stringify(dropZones) === JSON.stringify(correctOrder);

      case 'drag-drop':
        return Array.isArray(selectedAnswer) && 
               Array.isArray(currentQuestion.correctAnswer) &&
               selectedAnswer.sort().join(',') === (currentQuestion.correctAnswer as string[]).sort().join(',');

      case 'image-selection':
        return selectedAnswer === currentQuestion.correctAnswer;

      default:
        return false;
    }
  };

  const nextQuestion = () => {
    if (isLastQuestion) {
      finishQuiz();
    } else {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const finishQuiz = () => {
    if (!user) return;

    const totalPoints = questions.reduce((acc, q) => acc + q.points, 0);
    const earnedPoints = answers.reduce((acc, answer) => {
      const question = questions.find(q => q.id === answer.questionId);
      return acc + (answer.correct ? (question?.points || 0) : 0);
    }, 0);

    const score = Math.round((earnedPoints / totalPoints) * 100);
    const passed = score >= 70; // 70% to pass

    const result: TestResult = {
      id: `test_${Date.now()}`,
      userId: user.id,
      trainingId: videoId,
      score,
      passed,
      completedAt: new Date(),
      answers,
      earnedXP: passed ? Math.floor(score * 2) : Math.floor(score * 0.5),
      earnedCoins: passed ? Math.floor(score / 5) : Math.floor(score / 10)
    };

    submitTestResult(result);
    onComplete(result);
  };

  // Handle drag and drop
  const handleDragStart = (e: React.DragEvent, item: string) => {
    e.dataTransfer.setData('text/plain', item);
  };

  const handleDrop = (e: React.DragEvent, dropIndex: number) => {
    e.preventDefault();
    const draggedItem = e.dataTransfer.getData('text/plain');

    if (currentQuestion.type === 'sorting') {
      const newDropZones = [...dropZones];
      newDropZones[dropIndex] = draggedItem;
      setDropZones(newDropZones);

      const newDraggedItems = draggedItems.filter(item => item !== draggedItem);
      setDraggedItems(newDraggedItems);

      if (newDropZones.filter(Boolean).length === currentQuestion.options?.length) {
        setSelectedAnswer(newDropZones);
      }
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
  };

  const renderQuestionInput = () => {
    switch (currentQuestion.type) {
      case 'multiple-choice':
        return (
          <div className="space-y-3">
            {currentQuestion.options?.map((option, index) => (
              <label
                key={index}
                className={cn(
                  "flex items-center p-4 border-2 rounded-lg cursor-pointer transition-all",
                  selectedAnswer === option
                    ? "border-blue-500 bg-blue-50"
                    : "border-gray-200 hover:border-gray-300"
                )}
              >
                <input
                  type="radio"
                  name="answer"
                  value={option}
                  checked={selectedAnswer === option}
                  onChange={(e) => setSelectedAnswer(e.target.value)}
                  className="mr-3"
                  disabled={isAnswered}
                />
                <span className="text-lg">{option}</span>
              </label>
            ))}
          </div>
        );

      case 'image-selection':
        return (
          <div className="grid grid-cols-2 gap-4">
            {currentQuestion.options?.map((option, index) => (
              <div
                key={index}
                className={cn(
                  "border-2 rounded-lg p-4 cursor-pointer transition-all",
                  selectedAnswer === option
                    ? "border-blue-500 bg-blue-50"
                    : "border-gray-200 hover:border-gray-300"
                )}
                onClick={() => !isAnswered && setSelectedAnswer(option)}
              >
                <img
                  src={`/quiz-images/${option}.jpg`}
                  alt={option}
                  className="w-full h-32 object-cover rounded mb-2"
                />
                <p className="text-center font-medium">{option}</p>
              </div>
            ))}
          </div>
        );

      case 'sorting':
        return (
          <div className="space-y-6">
            <div className="space-y-2">
              <h4 className="font-medium text-gray-700">Zu sortierende Elemente:</h4>
              <div className="flex flex-wrap gap-2">
                {draggedItems.map((item, index) => (
                  <div
                    key={index}
                    draggable
                    onDragStart={(e) => handleDragStart(e, item)}
                    className="bg-blue-100 text-blue-800 px-3 py-2 rounded-lg cursor-move border-2 border-blue-200 hover:bg-blue-200 transition-colors"
                  >
                    {item}
                  </div>
                ))}
              </div>
            </div>

            <div className="space-y-2">
              <h4 className="font-medium text-gray-700">Richtige Reihenfolge:</h4>
              <div className="space-y-2">
                {Array.from({ length: currentQuestion.options?.length || 0 }).map((_, index) => (
                  <div
                    key={index}
                    onDrop={(e) => handleDrop(e, index)}
                    onDragOver={handleDragOver}
                    className={cn(
                      "border-2 border-dashed p-4 rounded-lg min-h-[60px] flex items-center",
                      dropZones[index]
                        ? "border-green-300 bg-green-50"
                        : "border-gray-300 bg-gray-50"
                    )}
                  >
                    {dropZones[index] ? (
                      <div className="bg-green-100 text-green-800 px-3 py-2 rounded-lg">
                        {dropZones[index]}
                      </div>
                    ) : (
                      <span className="text-gray-400">Position {index + 1}</span>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </div>
        );

      case 'drag-drop':
        return (
          <div className="space-y-4">
            <p className="text-gray-600">W√§hlen Sie alle zutreffenden Antworten:</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {currentQuestion.options?.map((option, index) => (
                <label
                  key={index}
                  className={cn(
                    "flex items-center p-4 border-2 rounded-lg cursor-pointer transition-all",
                    Array.isArray(selectedAnswer) && selectedAnswer.includes(option)
                      ? "border-blue-500 bg-blue-50"
                      : "border-gray-200 hover:border-gray-300"
                  )}
                >
                  <input
                    type="checkbox"
                    value={option}
                    checked={Array.isArray(selectedAnswer) && selectedAnswer.includes(option)}
                    onChange={(e) => {
                      const current = Array.isArray(selectedAnswer) ? selectedAnswer : [];
                      if (e.target.checked) {
                        setSelectedAnswer([...current, option]);
                      } else {
                        setSelectedAnswer(current.filter(item => item !== option));
                      }
                    }}
                    className="mr-3"
                    disabled={isAnswered}
                  />
                  <span className="text-lg">{option}</span>
                </label>
              ))}
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      {/* Progress */}
      <div className="mb-6">
        <div className="flex justify-between items-center mb-2">
          <span className="text-sm text-gray-600">
            Frage {currentQuestionIndex + 1} von {questions.length}
          </span>
          <span className="text-sm text-gray-600">
            {currentQuestion.points} Punkte
          </span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-blue-500 h-2 rounded-full transition-all duration-300"
            style={{ width: `${((currentQuestionIndex + 1) / questions.length) * 100}%` }}
          />
        </div>
      </div>

      {/* Question */}
      <div className="mb-6">
        <h3 className="text-xl font-bold text-gray-900 mb-4">
          {currentQuestion.question}
        </h3>

        {currentQuestion.mediaUrl && (
          <img
            src={currentQuestion.mediaUrl}
            alt="Question media"
            className="w-full max-w-md mx-auto rounded-lg mb-4"
          />
        )}

        {renderQuestionInput()}
      </div>

      {/* Explanation */}
      {showExplanation && currentQuestion.explanation && (
        <div className={cn(
          "p-4 rounded-lg mb-6",
          checkAnswer() ? "bg-green-50 border border-green-200" : "bg-red-50 border border-red-200"
        )}>
          <div className="flex items-start gap-3">
            <span className="text-2xl">
              {checkAnswer() ? '‚úÖ' : '‚ùå'}
            </span>
            <div>
              <p className="font-medium mb-2">
                {checkAnswer() ? 'Richtig!' : 'Leider falsch'}
              </p>
              <p className="text-gray-700">{currentQuestion.explanation}</p>
            </div>
          </div>
        </div>
      )}

      {/* Actions */}
      <div className="flex justify-between">
        <button
          disabled={currentQuestionIndex === 0}
          onClick={() => setCurrentQuestionIndex(currentQuestionIndex - 1)}
          className="px-4 py-2 text-gray-600 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
        >
          Zur√ºck
        </button>

        {!isAnswered ? (
          <button
            onClick={handleAnswer}
            disabled={!selectedAnswer || (Array.isArray(selectedAnswer) && selectedAnswer.length === 0)}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed font-medium"
          >
            Antworten
          </button>
        ) : (
          <button
            onClick={nextQuestion}
            className="px-6 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 font-medium"
          >
            {isLastQuestion ? 'Quiz beenden' : 'Weiter'}
          </button>
        )}
      </div>
    </div>
  );
};

import React, { useState, useMemo } from 'react';
import { CalendarEntry, colorMap, abbreviationMap, getWorkTimeColor } from '../types/calendar';
import { cn } from '../utils/cn';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameDay, isToday, startOfYear, endOfYear } from 'date-fns';
import { de } from 'date-fns/locale';

interface TeamCalendarViewProps {
  view: 'monat' | 'jahr';
  entries: CalendarEntry[];
  users: { userId: string; userName: string }[];
  onCellClick?: (userId: string, date: string) => void;
  isAdmin?: boolean;
}

/**
 * TeamCalendarView Component
 * Displays a scrollable calendar view with absence status for all team members
 * Each user has a horizontal row, each day has a cell with info & color
 */
export const TeamCalendarView: React.FC<TeamCalendarViewProps> = ({
  view,
  entries,
  users,
  onCellClick,
  isAdmin = false
}) => {
  const [selectedMonth, setSelectedMonth] = useState(new Date());
  const [hoveredCell, setHoveredCell] = useState<{ userId: string; date: string } | null>(null);
  const [filterType, setFilterType] = useState<string>('all');

  /**
   * Generate date range based on current view (month or year)
   * For month view: all days in the selected month
   * For year view: all days in the selected year
   */
  const dateRange = useMemo(() => {
    if (view === 'monat') {
      const start = startOfMonth(selectedMonth);
      const end = endOfMonth(selectedMonth);
      return eachDayOfInterval({ start, end });
    } else {
      const start = startOfYear(selectedMonth);
      const end = endOfYear(selectedMonth);
      return eachDayOfInterval({ start, end });
    }
  }, [view, selectedMonth]);

  /**
   * Group entries by userId and date for efficient lookup
   * Key format: "userId-yyyy-MM-dd"
   */
  const entriesByUserAndDate = useMemo(() => {
    const map = new Map<string, CalendarEntry>();
    entries.forEach(entry => {
      const key = `${entry.userId}-${entry.date}`;
      map.set(key, entry);
    });
    return map;
  }, [entries]);

  /**
   * Filter entries based on selected type
   * Returns all entries if filter is 'all', otherwise only matching types
   */
  const filteredEntries = useMemo(() => {
    if (filterType === 'all') return entriesByUserAndDate;

    const filtered = new Map<string, CalendarEntry>();
    entriesByUserAndDate.forEach((entry, key) => {
      if (entry.type === filterType) {
        filtered.set(key, entry);
      }
    });
    return filtered;
  }, [entriesByUserAndDate, filterType]);

  /**
   * Get calendar entry for a specific user and date
   * @param userId - The user's ID
   * @param date - The date to check
   * @returns The calendar entry if found, undefined otherwise
   */
  const getEntry = (userId: string, date: Date): CalendarEntry | undefined => {
    const dateStr = format(date, 'yyyy-MM-dd');
    return filteredEntries.get(`${userId}-${dateStr}`);
  };

  /**
   * Generate cell content with appropriate styling based on entry type
   * @param entry - The calendar entry to display
   * @returns JSX element with styled abbreviation or null
   */
  const getCellContent = (entry: CalendarEntry | undefined) => {
    if (!entry) return null;

    const abbreviation = entry.type === 'zeit' 
      ? abbreviationMap.zeit(entry.stunden)
      : abbreviationMap[entry.type];

    const backgroundColor = entry.type === 'zeit'
      ? getWorkTimeColor(entry.stunden)
      : colorMap[entry.type];

    const textColor = entry.type === 'krank' ? 'white' : 'black';

    return (
      <div 
        className={cn(
          "absolute bottom-0 left-0 right-0 text-xs font-medium text-center py-0.5 rounded-sm",
          entry.status === 'beantragt' && "opacity-60 border border-dashed border-gray-400"
        )}
        style={{ backgroundColor, color: textColor }}
      >
        {abbreviation}
      </div>
    );
  };

  /**
   * Generate tooltip content for calendar entries
   * @param entry - The calendar entry
   * @returns Formatted string with entry details
   */
  const getTooltipContent = (entry: CalendarEntry) => {
    let content = `${entry.type.charAt(0).toUpperCase() + entry.type.slice(1)}`;
    if (entry.type === 'zeit' && entry.stunden) {
      content += ` (${entry.stunden}h)`;
    }
    if (entry.status) {
      content += ` - ${entry.status}`;
    }
    return content;
  };

  /**
   * Navigate to previous or next month
   * @param direction - Positive for next month, negative for previous
   */
  const navigateMonth = (direction: number) => {
    const newDate = new Date(selectedMonth);
    newDate.setMonth(newDate.getMonth() + direction);
    setSelectedMonth(newDate);
  };

  return (
    <div className="w-full">
      {/* Header Controls */}
      <div className="mb-4 flex items-center justify-between">
        <div className="flex items-center gap-4">
          {view === 'monat' && (
            <div className="flex items-center gap-2">
              <button
                onClick={() => navigateMonth(-1)}
                className="p-2 hover:bg-gray-100 rounded-lg"
              >
                ‚Üê
              </button>
              <h3 className="text-lg font-semibold">
                {format(selectedMonth, 'MMMM yyyy', { locale: de })}
              </h3>
              <button
                onClick={() => navigateMonth(1)}
                className="p-2 hover:bg-gray-100 rounded-lg"
              >
                ‚Üí
              </button>
            </div>
          )}
          {view === 'jahr' && (
            <h3 className="text-lg font-semibold">
              {format(selectedMonth, 'yyyy')}
            </h3>
          )}
        </div>

        {/* Filter Controls */}
        <div className="flex items-center gap-2">
          <label className="text-sm text-gray-600">Filter:</label>
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="px-3 py-1 border border-gray-300 rounded-lg text-sm"
          >
            <option value="all">Alle</option>
            <option value="urlaub">Urlaub</option>
            <option value="krank">Krankheit</option>
            <option value="meeting">Meeting</option>
            <option value="fortbildung">Fortbildung</option>
            <option value="ux">Sonderurlaub</option>
            <option value="zeit">Arbeitszeit</option>
          </select>
        </div>
      </div>

      {/* Calendar Grid */}
      <div className={cn(
        "overflow-x-auto border border-gray-200 rounded-lg",
        view === 'jahr' && "max-w-full"
      )}>
        <table className="w-full border-collapse">
          <thead>
            <tr>
              <th className="sticky left-0 bg-white z-10 p-2 border-b border-r border-gray-200 text-left text-sm font-medium text-gray-700 min-w-[150px]">
                Mitarbeiter
              </th>
              {view === 'monat' && dateRange.map((date) => (
                <th
                  key={date.toISOString()}
                  className={cn(
                    "p-1 border-b border-gray-200 text-center text-xs font-medium text-gray-600 min-w-[40px]",
                    isToday(date) && "bg-blue-50"
                  )}
                >
                  <div>{format(date, 'EEE', { locale: de })}</div>
                  <div className="text-sm font-semibold">{format(date, 'd')}</div>
                </th>
              ))}
              {view === 'jahr' && (
                <>
                  {/* Month headers for year view */}
                  {Array.from({ length: 12 }, (_, i) => {
                    const monthDate = new Date(selectedMonth.getFullYear(), i, 1);
                    const daysInMonth = new Date(selectedMonth.getFullYear(), i + 1, 0).getDate();
                    return (
                      <th
                        key={i}
                        colSpan={daysInMonth}
                        className="p-2 border-b border-gray-200 text-center text-sm font-medium text-gray-700"
                      >
                        {format(monthDate, 'MMM', { locale: de })}
                      </th>
                    );
                  })}
                </>
              )}
            </tr>
            {view === 'jahr' && (
              <tr>
                <th className="sticky left-0 bg-white z-10 border-b border-r border-gray-200"></th>
                {dateRange.map((date) => (
                  <th
                    key={date.toISOString()}
                    className={cn(
                      "p-0.5 border-b border-gray-200 text-center text-xs font-normal text-gray-500 min-w-[25px]",
                      isToday(date) && "bg-blue-50"
                    )}
                  >
                    {format(date, 'd')}
                  </th>
                ))}
              </tr>
            )}
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.userId} className="hover:bg-gray-50">
                <td className="sticky left-0 bg-white z-10 p-2 border-r border-gray-200 text-sm font-medium text-gray-900 whitespace-nowrap">
                  {user.userName}
                </td>
                {dateRange.map((date) => {
                  const entry = getEntry(user.userId, date);
                  const cellKey = `${user.userId}-${format(date, 'yyyy-MM-dd')}`;
                  const isHovered = hoveredCell?.userId === user.userId && hoveredCell?.date === format(date, 'yyyy-MM-dd');

                  return (
                    <td
                      key={date.toISOString()}
                      className={cn(
                        "relative p-0 border border-gray-100 cursor-pointer transition-colors",
                        view === 'monat' ? "h-12" : "h-8",
                        isToday(date) && "bg-blue-50",
                        isHovered && "bg-gray-100",
                        date.getDay() === 0 || date.getDay() === 6 ? "bg-gray-50" : ""
                      )}
                      onClick={() => onCellClick?.(user.userId, format(date, 'yyyy-MM-dd'))}
                      onMouseEnter={() => setHoveredCell({ userId: user.userId, date: format(date, 'yyyy-MM-dd') })}
                      onMouseLeave={() => setHoveredCell(null)}
                    >
                      {view === 'monat' && (
                        <div className="text-xs text-gray-400 text-center mt-0.5">
                          {format(date, 'd')}
                        </div>
                      )}
                      {getCellContent(entry)}
                      {entry && isHovered && (
                        <div className="absolute z-20 bottom-full left-1/2 transform -translate-x-1/2 mb-1 px-2 py-1 bg-gray-800 text-white text-xs rounded whitespace-nowrap">
                          {getTooltipContent(entry)}
                        </div>
                      )}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Legend */}
      <div className="mt-4 flex flex-wrap gap-4 text-sm">
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.urlaub }}></div>
          <span>Urlaub</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.krank }}></div>
          <span>Krankheit</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.meeting }}></div>
          <span>Meeting</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.fortbildung }}></div>
          <span>Fortbildung</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.ux }}></div>
          <span>Sonderurlaub</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.zeit.vollzeit }}></div>
          <span>Vollzeit (8h+)</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.zeit.teilzeit }}></div>
          <span>Teilzeit (6-8h)</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-4 rounded" style={{ backgroundColor: colorMap.zeit.unter6h }}></div>
          <span>Unter 6h</span>
        </div>
      </div>
    </div>
  );
};

import React, { useEffect, useRef, useState } from 'react';
import { useLearningStore } from '../state/learning';
import { VideoContent } from '../types/learning';
import { cn } from '../utils/cn';

interface VideoPlayerProps {
  video: VideoContent;
  onComplete?: () => void;
}

export const VideoPlayer: React.FC<VideoPlayerProps> = ({ video, onComplete }) => {
  const { updateProgress, completeVideo } = useLearningStore();
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [showTranscript, setShowTranscript] = useState(false);
  const [playbackSpeed, setPlaybackSpeed] = useState(1);
  const videoRef = useRef<HTMLVideoElement>(null);
  const progressInterval = useRef<number | undefined>(undefined);

  useEffect(() => {
    const videoElement = videoRef.current;
    if (!videoElement) return;

    const handleTimeUpdate = () => {
      setCurrentTime(videoElement.currentTime);

      // Update progress every 5 seconds
      if (Math.floor(videoElement.currentTime) % 5 === 0) {
        updateProgress(video.id, Math.floor(videoElement.currentTime));
      }
    };

    const handleEnded = () => {
      completeVideo(video.id);
      if (onComplete) onComplete();
    };

    videoElement.addEventListener('timeupdate', handleTimeUpdate);
    videoElement.addEventListener('ended', handleEnded);
    videoElement.addEventListener('play', () => setIsPlaying(true));
    videoElement.addEventListener('pause', () => setIsPlaying(false));

    return () => {
      videoElement.removeEventListener('timeupdate', handleTimeUpdate);
      videoElement.removeEventListener('ended', handleEnded);
      videoElement.removeEventListener('play', () => setIsPlaying(true));
      videoElement.removeEventListener('pause', () => setIsPlaying(false));
    };
  }, [video.id, updateProgress, completeVideo, onComplete]);

  const togglePlayPause = () => {
    if (videoRef.current) {
      if (isPlaying) {
        videoRef.current.pause();
      } else {
        videoRef.current.play();
      }
    }
  };

  const skipTime = (seconds: number) => {
    if (videoRef.current) {
      videoRef.current.currentTime += seconds;
    }
  };

  const changeSpeed = () => {
    const speeds = [0.5, 0.75, 1, 1.25, 1.5, 2];
    const currentIndex = speeds.indexOf(playbackSpeed);
    const nextIndex = (currentIndex + 1) % speeds.length;
    const newSpeed = speeds[nextIndex];

    setPlaybackSpeed(newSpeed);
    if (videoRef.current) {
      videoRef.current.playbackRate = newSpeed;
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const getActiveTranscriptSegment = () => {
    if (!video.transcription) return null;

    return video.transcription.segments.find(
      segment => currentTime >= segment.start && currentTime <= segment.end
    );
  };

  const activeSegment = getActiveTranscriptSegment();

  // For YouTube videos
  const isYouTube = video.url.includes('youtube.com') || video.url.includes('youtu.be');
  const youtubeId = isYouTube ? video.url.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/)([^&\n?#]+)/)?.[1] : null;

  return (
    <div className="bg-black rounded-xl overflow-hidden">
      {/* Video Container */}
      <div className="relative aspect-video bg-gray-900">
        {isYouTube && youtubeId ? (
          <iframe
            className="w-full h-full"
            src={`https://www.youtube.com/embed/${youtubeId}?enablejsapi=1`}
            title={video.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          />
        ) : (
          <video
            ref={videoRef}
            className="w-full h-full"
            src={video.url}
            poster={video.thumbnail}
          />
        )}

        {/* Custom Controls Overlay */}
        {!isYouTube && (
          <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4">
            {/* Progress Bar */}
            <div className="mb-3">
              <div className="bg-gray-600 h-1 rounded-full overflow-hidden cursor-pointer"
                onClick={(e) => {
                  if (videoRef.current) {
                    const rect = e.currentTarget.getBoundingClientRect();
                    const percent = (e.clientX - rect.left) / rect.width;
                    videoRef.current.currentTime = percent * video.duration;
                  }
                }}
              >
                <div 
                  className="bg-blue-500 h-full"
                  style={{ width: `${(currentTime / video.duration) * 100}%` }}
                />
              </div>
            </div>

            {/* Controls */}
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <button
                  onClick={togglePlayPause}
                  className="text-white hover:text-blue-400 transition-colors"
                >
                  {isPlaying ? (
                    <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zM7 8a1 1 0 012 0v4a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v4a1 1 0 102 0V8a1 1 0 00-1-1z" clipRule="evenodd" />
                    </svg>
                  ) : (
                    <svg className="w-8 h-8" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z" clipRule="evenodd" />
                    </svg>
                  )}
                </button>

                <button
                  onClick={() => skipTime(-10)}
                  className="text-white hover:text-blue-400 transition-colors"
                >
                  <span className="text-sm font-medium">-10s</span>
                </button>

                <button
                  onClick={() => skipTime(10)}
                  className="text-white hover:text-blue-400 transition-colors"
                >
                  <span className="text-sm font-medium">+10s</span>
                </button>

                <div className="text-white text-sm">
                  {formatTime(currentTime)} / {formatTime(video.duration)}
                </div>
              </div>

              <div className="flex items-center gap-3">
                <button
                  onClick={changeSpeed}
                  className="text-white hover:text-blue-400 transition-colors text-sm font-medium"
                >
                  {playbackSpeed}x
                </button>

                <button
                  onClick={() => setShowTranscript(!showTranscript)}
                  className={cn(
                    "text-white hover:text-blue-400 transition-colors",
                    showTranscript && "text-blue-400"
                  )}
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                  </svg>
                </button>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Transcript Panel */}
      {showTranscript && video.transcription && (
        <div className="bg-gray-900 p-4 max-h-48 overflow-y-auto">
          <h3 className="text-white font-semibold mb-3">Transkript</h3>
          <div className="space-y-2">
            {video.transcription.segments.map((segment, index) => (
              <div
                key={index}
                className={cn(
                  "p-2 rounded cursor-pointer transition-colors",
                  activeSegment === segment
                    ? "bg-blue-600/20 text-blue-300"
                    : "text-gray-400 hover:bg-gray-800"
                )}
                onClick={() => {
                  if (videoRef.current) {
                    videoRef.current.currentTime = segment.start;
                  }
                }}
              >
                <div className="text-xs text-gray-500 mb-1">
                  {formatTime(segment.start)} - {formatTime(segment.end)}
                </div>
                <div className="text-sm">{segment.text}</div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Active Subtitle */}
      {activeSegment && !showTranscript && (
        <div className="absolute bottom-20 left-0 right-0 text-center px-4">
          <div className="inline-block bg-black/80 text-white px-4 py-2 rounded-lg">
            <p className="text-lg">{activeSegment.text}</p>
          </div>
        </div>
      )}
    </div>
  );
};

