const MainLayout = ({ children }: { children: React.ReactNode }) => {
  const location = useLocation();
  const { user } = useAuthStore();
  const { getUnreadCount } = useNotificationsStore();
  const { getAllLeaveRequests } = useLeavesStore();

  const getNotificationCount = (tabPath: string): number => {
    if (!user) return 0;

    const isAdmin = user.role === 'ADMIN' || user.role === 'SUPERADMIN';

    switch (tabPath) {
      case '/requests':
        if (isAdmin) {
          // For admin: show pending leave requests (these are the actionable items)
          const pendingRequests = getAllLeaveRequests().filter(req => req.status === 'PENDING').length;
          return pendingRequests;
        } else {
          // For users: show unread notifications about their requests
          return getUnreadCount(user.id);
        }
      case '/training':
        // Could add training-related notifications here
        return 0;
      default:
        return 0;
    }
  };

  const isAdmin = user?.role === 'ADMIN' || user?.role === 'SUPERADMIN';

  const tabs = [
    { path: '/dashboard', label: '√úbersicht', icon: 'üè†' },
    { path: '/requests', label: 'Antr√§ge', icon: 'üìù' },
    { path: '/time', label: 'Zeiten', icon: '‚è∞' },
    { path: '/calendar', label: 'Urlaub', icon: 'üìÖ' },
    { path: '/learning', label: 'Lernen', icon: 'üéì' },
    { path: '/benefits', label: 'Benefits', icon: 'üéÅ' },
    { path: '/documents', label: 'Dokumente', icon: 'üìÑ' },
    ...(isAdmin ? [{ path: '/team-management', label: 'Teams', icon: 'üë•' }] : []),
  ];

  return (
    <div className="min-h-screen flex flex-col">
      <nav className="bg-white border-b border-gray-200 shadow-sm">
        <div className="flex items-center h-16 px-4">
          {/* HRdiese Logo */}
          <div className="flex items-center mr-8">
            <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center mr-2">
              <span className="text-white font-bold text-sm">HR</span>
            </div>
            <span className="text-lg font-semibold text-gray-900 hidden sm:inline">HRdiese</span>
          </div>

          {/* Navigation Tabs */}
          <div className="flex justify-around items-center flex-1">
            {tabs.map((tab) => {
            const isActive = location.pathname.startsWith(tab.path);
            const notificationCount = getNotificationCount(tab.path);
            return (
              <Link
                key={tab.path}
                to={tab.path}
                className={cn(
                  "flex items-center justify-center px-3 py-2 text-sm font-medium rounded-lg transition-colors relative",
                  isActive 
                    ? "text-blue-600 bg-blue-50" 
                    : "text-gray-600 hover:text-gray-900 hover:bg-gray-50"
                )}
              >
                <span className="text-lg mr-2">{tab.icon}</span>
                <span className="hidden sm:inline">{tab.label}</span>
                {notificationCount > 0 && (
                  <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
                    {notificationCount > 99 ? '99+' : notificationCount}
                  </span>
                )}
              </Link>
            );
            })}
          </div>
        </div>
      </nav>
      <main className="flex-1">
        {children}
      </main>
    </div>
  );
};

export const AppNavigator = () => {
  const { isAuthenticated } = useAuthStore();

  if (!isAuthenticated) {
    return (
      <Routes>
        <Route path="/login" element={<LoginScreen />} />
        <Route path="*" element={<Navigate to="/login" />} />
