          <div className="space-y-4">
            {(requestsTab === 'my' ? 
              allLeaves.filter(leave => leave.userId === user.id) :
              allLeaves
            )
              .sort((a, b) => new Date(b.createdAt || '').getTime() - new Date(a.createdAt || '').getTime())
              .slice(0, 8)
              .map(leave => {
                const start = new Date(leave.startDate);
                const end = new Date(leave.endDate);
                const days = Math.ceil((end.getTime() - start.getTime()) / (1000 * 60 * 60 * 24)) + 1;
                const hasNotification = hasUnreadNotification(leave.id);

                return (
                  <div 
                    key={leave.id} 
                    className={cn(
                      "p-4 border border-gray-200 rounded-lg cursor-pointer hover:bg-gray-50 transition-colors relative",
                      hasNotification && "bg-blue-50 border-blue-200"
                    )}
                    onClick={() => handleLeaveRequestClick(leave.id)}
                  >
                    {/* Notification Indicator */}
                    {hasNotification && (
                      <div className="absolute -top-1 -right-1 w-3 h-3 bg-red-500 rounded-full"></div>
                    )}

                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3">
                        <span className="text-2xl">
                          {leave.type === 'VACATION' ? 'üèñÔ∏è' : 'üè•'}
                        </span>
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <p className="font-semibold text-gray-900">
                              {requestsTab === 'manage' ? `${getUserName(leave.userId)} - ` : ''}
                              {leave.type === 'VACATION' ? 'Urlaub' : 'Krankheit'}
                            </p>
                            {hasNotification && (
                              <span className="text-xs text-blue-600 bg-blue-100 px-2 py-1 rounded">
                                üîî Neu
                              </span>
                            )}
                          </div>

                          <p className="text-sm text-gray-600 mb-2">
                            {format(start, 'dd.MM.yyyy')} - {format(end, 'dd.MM.yyyy')} ({days} Tag{days !== 1 ? 'e' : ''})
                          </p>

                          {leave.comment && (
                            <p className="text-sm text-gray-600 italic mb-2">"{leave.comment}"</p>
                          )}

                          <div className="text-xs text-gray-500 space-y-1">
                            <p>Erstellt: {format(new Date(leave.createdAt), 'dd.MM.yyyy HH:mm')} von {getUserName(leave.userId)}</p>
                            {leave.approvedAt && leave.approvedBy && (
                              <p>
                                {leave.status === 'APPROVED' ? 'Genehmigt' : 'Abgelehnt'}: {format(new Date(leave.approvedAt), 'dd.MM.yyyy HH:mm')} von {getUserName(leave.approvedBy)}
                              </p>
                            )}
                          </div>
                        </div>
                      </div>

                      <span className={cn(
                        "px-3 py-1 rounded-full text-xs font-medium",
                        leave.status === 'APPROVED' ? 'bg-green-100 text-green-800' :
                        leave.status === 'PENDING' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-red-100 text-red-800'
                      )}>
                        {leave.status === 'APPROVED' ? 'Genehmigt' :
                         leave.status === 'PENDING' ? 'Ausstehend' : 'Abgelehnt'}
                      </span>
                    </div>
                  </div>
                );
              })}

            {(requestsTab === 'my' ? 
              allLeaves.filter(leave => leave.userId === user.id) :
              allLeaves
            ).length === 0 && (
              <p className="text-gray-500 text-center py-4">
                Noch keine Abwesenheiten vorhanden
              </p>
            )}
          </div>
        </div>

        {/* Detail Modal for Work Time */}
        {showDetailModal && selectedDay && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-xl p-6 max-w-md w-full">
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold">
                  Arbeitszeit - {format(selectedDay, 'dd.MM.yyyy', { locale: de })}
                </h3>
                <button
                  onClick={() => setShowDetailModal(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  ‚úï
                </button>
              </div>

              {(() => {
                const dateStr = format(selectedDay, 'yyyy-MM-dd');
                const timeRecord = getTimeRecordsForPeriod(user!.id, dateStr, dateStr)[0];

                if (!timeRecord) {
                  return (
                    <p className="text-gray-500 text-center py-4">
                      Keine Arbeitszeit f√ºr diesen Tag gefunden
                    </p>
                  );
                }

                return (
                  <div className="space-y-4">
                    <div className="bg-gray-50 rounded-lg p-4">
                      <div className="grid grid-cols-2 gap-4 text-sm">
                        <div>
                          <span className="text-gray-600">Arbeitsbeginn:</span>
                          <p className="font-mono font-medium">{timeRecord.timeIn}</p>
                        </div>
                        <div>
                          <span className="text-gray-600">Arbeitsende:</span>
                          <p className="font-mono font-medium">{timeRecord.timeOut || 'l√§uft'}</p>
                        </div>
                        <div>
                          <span className="text-gray-600">Pause:</span>
                          <p className="font-mono font-medium">{timeRecord.breakMinutes}min</p>
                        </div>
                        <div>
                          <span className="text-gray-600">Gesamtzeit:</span>
                          <p className="font-mono font-medium">{timeRecord.totalHours.toFixed(1)}h</p>
                        </div>
                      </div>
                    </div>

                    <div className="text-center">
                      <span className={cn(
                        "inline-block px-3 py-1 rounded-full text-sm font-medium",
                        timeRecord.totalHours >= 8 ? "bg-green-100 text-green-800" :
                        timeRecord.totalHours >= 6 ? "bg-yellow-100 text-yellow-800" :
                        "bg-red-100 text-red-800"
                      )}>
                        {timeRecord.totalHours >= 8 ? "Vollzeit erreicht" :
                         timeRecord.totalHours >= 6 ? "Teilzeit" : "Unterzeit"}
                      </span>
                    </div>
                  </div>
                );
              })()}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useLearningStore } from '../state/learning';
import { useAuthStore } from '../state/auth';
import { VideoPlayer } from '../components/VideoPlayer';
import { Quiz } from '../components/Quiz';
import { LootboxAnimation, CelebrationOverlay } from '../components/LootboxAnimation';
import { TestResult } from '../types/learning';
import { cn } from '../utils/cn';

export const VideoLearningScreen = () => {
  const { videoId } = useParams<{ videoId: string }>();
  const navigate = useNavigate();
  const { user } = useAuthStore();
  const { 
    videos, 
    currentVideo, 
    selectVideo, 
    userProgress,
    getVideoQuestions,
    userLevel,
    userCoins,
    addXP,
    awardBadge
  } = useLearningStore();

  const [stage, setStage] = useState<'video' | 'quiz' | 'completed'>('video');
  const [showRewards, setShowRewards] = useState(false);
  const [earnedRewards, setEarnedRewards] = useState<any[]>([]);
  const [showCelebration, setCelebration] = useState(false);

  useEffect(() => {
    if (videoId) {
      selectVideo(videoId);
    }
  }, [videoId, selectVideo]);

  useEffect(() => {
    // Check if video is completed and determine next stage
    if (currentVideo && user) {
      const progress = userProgress[currentVideo.id];
      const questions = getVideoQuestions(currentVideo.id);

      if (progress?.completed && questions.length > 0 && stage === 'video') {
        setStage('quiz');
      }
    }
  }, [currentVideo, user, userProgress, stage, getVideoQuestions]);

  const video = currentVideo || videos.find(v => v.id === videoId);

  if (!video) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Video nicht gefunden</h1>
          <p className="text-gray-600 mb-4">Das angeforderte Video existiert nicht.</p>
          <button
            onClick={() => navigate('/learning')}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            Zur√ºck zur √úbersicht
          </button>
        </div>
      </div>
    );
  }

  const questions = getVideoQuestions(video.id);
  const progress = user ? userProgress[video.id] : null;
  const isVideoCompleted = progress?.completed || false;
  const hasQuestions = questions.length > 0;

  const handleVideoComplete = () => {
    const rewards = [
      { type: 'xp', value: 50, description: 'Video abgeschlossen', icon: '‚≠ê' },
      { type: 'coins', value: 10, description: 'BrowoCoins', icon: 'ü™ô' }
    ];

    // Check for early bird badge (before 8 AM)
    const currentHour = new Date().getHours();
    if (currentHour < 8) {
      awardBadge('early-bird');
      rewards.push({
        type: 'badge',
        value: 100,
        description: 'Fr√ºhaufsteher Badge',
        icon: 'üåÖ'
      });
    }

    setEarnedRewards(rewards);

    if (hasQuestions) {
      setStage('quiz');
    } else {
      setStage('completed');
      setShowRewards(true);
    }
  };

  const handleQuizComplete = (result: TestResult) => {
    const baseRewards = [
      { type: 'xp', value: result.earnedXP, description: `${result.earnedXP} XP`, icon: '‚≠ê' },
      { type: 'coins', value: result.earnedCoins, description: `${result.earnedCoins} BrowoCoins`, icon: 'ü™ô' }
    ];

    // Perfect score bonus
    if (result.score === 100) {
      baseRewards.push({
        type: 'badge',
        value: 150,
        description: 'Perfektionist Badge',
        icon: 'üéØ'
      });
      baseRewards.push({
        type: 'coins',
        value: 50,
        description: 'Perfektion Bonus',
        icon: 'ü™ô'
      });
    }

    setEarnedRewards([...earnedRewards, ...baseRewards]);
    setStage('completed');
    setShowRewards(true);
  };

  const handleRewardsComplete = () => {
    setShowRewards(false);
    setCelebration(true);
  };

  const handleCelebrationComplete = () => {
    setCelebration(false);
    // Navigate back to learning dashboard
    navigate('/learning');
  };

  return (
    <div className="min-h-screen bg-gray-900">
      {/* Header */}
      <div className="bg-black bg-opacity-50 text-white p-4">
        <div className="flex items-center justify-between max-w-6xl mx-auto">
          <div className="flex items-center gap-4">
            <button
              onClick={() => navigate('/learning')}
              className="text-gray-300 hover:text-white transition-colors"
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>
            <div>
              <h1 className="text-xl font-bold">{video.title}</h1>
              <p className="text-gray-300 text-sm">{video.description}</p>
            </div>
          </div>

          {/* Progress Indicator */}
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <span className="text-yellow-400">ü™ô</span>
              <span className="font-bold">{userCoins}</span>
            </div>
            <div className="flex items-center gap-2">
              <span className="text-purple-400">‚≠ê</span>
              <span className="font-bold">{userLevel?.xp || 0}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Stage Indicator */}
      <div className="bg-gray-800 p-4">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center justify-center gap-8">
            <div className={cn(
              "flex items-center gap-2 px-4 py-2 rounded-full transition-colors",
              stage === 'video' 
                ? "bg-blue-600 text-white" 
                : isVideoCompleted
                ? "bg-green-600 text-white"
                : "bg-gray-600 text-gray-300"
            )}>
              <span>{isVideoCompleted ? '‚úì' : '‚ñ∂Ô∏è'}</span>
              <span>Video ansehen</span>
            </div>

            {hasQuestions && (
              <>
                <div className="w-8 h-0.5 bg-gray-600" />
                <div className={cn(
                  "flex items-center gap-2 px-4 py-2 rounded-full transition-colors",
                  stage === 'quiz'
                    ? "bg-blue-600 text-white"
                    : stage === 'completed'
                    ? "bg-green-600 text-white"
                    : "bg-gray-600 text-gray-300"
                )}>
                  <span>{stage === 'completed' ? '‚úì' : 'üìù'}</span>
                  <span>Quiz</span>
                </div>
              </>
            )}

            <div className="w-8 h-0.5 bg-gray-600" />
            <div className={cn(
              "flex items-center gap-2 px-4 py-2 rounded-full transition-colors",
              stage === 'completed'
                ? "bg-green-600 text-white"
                : "bg-gray-600 text-gray-300"
            )}>
              <span>{stage === 'completed' ? 'üèÜ' : 'üéÅ'}</span>
              <span>Belohnung</span>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-6xl mx-auto p-6">
        {stage === 'video' && (
          <div className="space-y-6">
            <VideoPlayer video={video} onComplete={handleVideoComplete} />

            {/* Video Info */}
            <div className="bg-gray-800 rounded-xl p-6 text-white">
              <div className="flex items-start justify-between">
                <div className="flex-1">
                  <h2 className="text-2xl font-bold mb-2">{video.title}</h2>
                  <p className="text-gray-300 mb-4">{video.description}</p>

                  <div className="flex items-center gap-6 text-sm">
                    <div className="flex items-center gap-2">
                      <span>‚è±Ô∏è</span>
                      <span>{Math.floor(video.duration / 60)} Minuten</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <span>üéØ</span>
                      <span>+50 XP</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <span>ü™ô</span>
                      <span>+10 M√ºnzen</span>
                    </div>
                    {hasQuestions && (
                      <div className="flex items-center gap-2">
                        <span>üìù</span>
                        <span>{questions.length} Fragen</span>
                      </div>
                    )}
                  </div>
                </div>

                {/* Progress */}
                {progress && (
                  <div className="ml-6">
                    <div className="text-right mb-2">
                      <span className="text-sm text-gray-400">Fortschritt</span>
                      <div className="text-xl font-bold">
                        {Math.round((progress.watchedSeconds / video.duration) * 100)}%
                      </div>
                    </div>
                    <div className="w-24 bg-gray-700 rounded-full h-2">
                      <div
                        className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                        style={{ width: `${(progress.watchedSeconds / video.duration) * 100}%` }}
                      />
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Transcript (if available) */}
            {video.transcription && (
              <div className="bg-gray-800 rounded-xl p-6 text-white">
                <h3 className="text-lg font-bold mb-4">üìÑ Vollst√§ndige Transkription</h3>
                <div className="max-h-48 overflow-y-auto text-gray-300 leading-relaxed">
                  {video.transcription.text}
                </div>
              </div>
            )}
          </div>
        )}

        {stage === 'quiz' && hasQuestions && (
          <div className="max-w-4xl mx-auto">
            <div className="bg-gray-800 rounded-xl p-6 text-white mb-6">
              <h2 className="text-2xl font-bold mb-2">üìù Wissenstest</h2>
              <p className="text-gray-300">
                Teste dein Wissen √ºber das Video und sammle zus√§tzliche Belohnungen!
              </p>
            </div>
            <Quiz
              videoId={video.id}
              questions={questions}
              onComplete={handleQuizComplete}
            />
          </div>
        )}

        {stage === 'completed' && (
          <div className="max-w-2xl mx-auto text-center">
            <div className="bg-gradient-to-br from-green-500 to-blue-600 rounded-2xl p-8 text-white">
              <div className="text-6xl mb-4">üéâ</div>
              <h2 className="text-3xl font-bold mb-2">Herzlichen Gl√ºckwunsch!</h2>
              <p className="text-xl mb-6">
                Du hast die Schulung "{video.title}" erfolgreich abgeschlossen!
              </p>

              {/* Summary */}
              <div className="bg-white bg-opacity-20 rounded-xl p-6 mb-6">
                <h3 className="text-lg font-bold mb-4">Deine Leistung</h3>
                <div className="grid grid-cols-2 gap-4 text-center">
                  <div>
                    <div className="text-2xl font-bold">100%</div>
                    <div className="text-sm opacity-90">Video angesehen</div>
                  </div>
                  {hasQuestions && (
                    <div>
                      <div className="text-2xl font-bold">
                        {Math.round(earnedRewards.find(r => r.type === 'quiz-score')?.value || 0)}%
                      </div>
                      <div className="text-sm opacity-90">Quiz-Ergebnis</div>
                    </div>
                  )}
                </div>
              </div>

              <button
                onClick={() => navigate('/learning')}
                className="bg-white text-blue-600 px-8 py-3 rounded-lg font-bold hover:bg-gray-100 transition-colors"
              >
                Weiter lernen
              </button>
            </div>
          </div>
        )}
      </div>

      {/* Reward Animations */}
      {showRewards && earnedRewards.length > 0 && (
        <LootboxAnimation
          reward={earnedRewards[0]}
          onComplete={handleRewardsComplete}
          autoStart
        />
      )}

      {showCelebration && (
        <CelebrationOverlay
          message="Schulung abgeschlossen!"
          icon="üéì"
          color="from-purple-400 to-blue-500"
          onComplete={handleCelebrationComplete}
        />
      )}
    </div>
  );
};

import React from 'react';
import { render, screen, fireEvent, waitFor } from '../../test-utils/test-utils';
import { AddEmployeeScreen } from '../AddEmployeeScreen';
import { useAuthStore } from '../../state/auth';
