import { create } from 'zustand';
import { persist, createJSONStorage } from 'zustand/middleware';
import { 
  Achievement, 
  UserAchievement, 
  AchievementCondition,
  ProgressTracker,
  PREDEFINED_ACHIEVEMENTS
} from '../types/gamification';
import { useAvatarStore } from './avatar';
import { useNotificationsStore } from './notifications';

interface AchievementsState {
  // Achievement system data
  achievements: Achievement[];
  userProgress: Record<string, ProgressTracker>;

  // Actions
  getAchievements: () => Achievement[];
  getUserAchievements: (_userId: string) => UserAchievement[];
  getUnlockedAchievements: (_userId: string) => Achievement[];
  getLockedAchievements: (_userId: string) => Achievement[];

  // Progress tracking
  updateProgress: (_userId: string, _eventType: string, _value: number, _metadata?: Record<string, any>) => void;
  checkAchievements: (_userId: string) => UserAchievement[];
  unlockAchievement: (_userId: string, _achievementId: string) => void;

  // Admin functions
  addAchievement: (achievement: Omit<Achievement, 'id'>) => void;
  updateAchievement: (_achievementId: string, _updates: Partial<Achievement>) => void;
  deleteAchievement: (_achievementId: string) => void;

  // Statistics
  getProgressStats: (_userId: string) => {
    totalAchievements: number;
    unlockedAchievements: number;
    completionRate: number;
    recentUnlocks: UserAchievement[];
  };

  // Additional methods
  createAchievement: (achievement: Achievement) => void;
  toggleAchievementActive: (_achievementId: string) => void;
  getAchievementStats: (_achievementId: string) => { unlockedCount: number };
  checkCondition: (_userId: string, condition: AchievementCondition, userProgress: ProgressTracker) => boolean;
  getTotalTrainingsCompleted: (_userId: string) => number;
  getTotalPunctualDays: (_userId: string) => number;
  getTotalCoinsEarned: (_userId: string) => number;
  getTotalFeedbackGiven: (_userId: string) => number;
}

export const useAchievementsStore = create<AchievementsState>()(
  persist(
    (set, get) => (_{
      achievements: PREDEFINED_ACHIEVEMENTS.map((achievement, index) => ({
        id: `predefined-${index}`,
        name: achievement.name,
        description: achievement.description,
        icon: achievement.icon,
        category: achievement.category,
        rarity: achievement.rarity,
        conditions: achievement.conditions.map(c => ({
          type: c.type as any,
          target: c.target,
          operator: c.operator as any,
          timeframe: c.timeframe
        })),
        xpReward: achievement.rewards ? (achievement.rewards as unknown as any[]).find(_(r: any) => r.type === 'xp')?.value as number || 50 : 50,
        rewards: achievement.rewards ? [...achievement.rewards] : undefined,
        isActive: true,
        isHidden: false,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      })),
      userProgress: {},

      getAchievements: () => get().achievements,

      getUserAchievements: (_userId: string) => {
        const avatarStore = useAvatarStore.getState();
        const userAvatar = avatarStore.getUserAvatar(userId);
        const achievements = get().achievements;

        if (!userAvatar?.achievements) return [];

        // Map user achievement IDs to full achievement objects
        return userAvatar.achievements.map(ua => {
          const achievement = achievements.find(a => a.id === ua.achievementId);
          if (!achievement) return null;

          return {
            id: `${userId}-${ua.achievementId}`,
            userId,
            achievementId: ua.achievementId,
            progress: 100,
            unlockedAt: ua.unlockedAt,
            notified: ua.isNew !== true,
            name: achievement.name,
            description: achievement.description,
            icon: achievement.icon,
            rarity: achievement.rarity
          };
        }).filter(Boolean) as UserAchievement[];
      },

      getUnlockedAchievements: (_userId: string) => {
        const userAchievements = get().getUserAchievements(userId);
        const allAchievements = get().achievements;

        const unlockedIds = userAchievements.map(ua => ua.achievementId);
        return allAchievements.filter(achievement => unlockedIds.includes(achievement.id));
      },

      getLockedAchievements: (_userId: string) => {
        const userAchievements = get().getUserAchievements(userId);
        const allAchievements = get().achievements;

        const unlockedIds = userAchievements.map(ua => ua.achievementId);
        return allAchievements.filter(achievement => 
          !unlockedIds.includes(achievement.id) && achievement.isActive && !achievement.isHidden
        );
      },

      updateProgress: (_userId: string, _eventType: string, _value: number, metadata = {}) => {
        const now = new Date().toISOString();
        const currentQuarter = `${new Date().getFullYear()}-Q${Math.ceil((new Date().getMonth() + 1) / 3)}`;

        set(state => {
          const userProgress = state.userProgress[userId] || {
            userId,
            achievements: {},
            dailyStreak: { current: 0, longest: 0, lastCheckin: '' },
            quarterlyStats: {
              quarter: currentQuarter,
              coinsEarned: 0,
              trainingsCompleted: 0,
              punctualDays: 0,
              feedbackGiven: 0
            }
          };

          // Update quarterly stats based on event type
          const quarterlyStats = { ...userProgress.quarterlyStats };
          if (quarterlyStats.quarter !== currentQuarter) {
            // Reset for new quarter
            quarterlyStats.quarter = currentQuarter;
            quarterlyStats.coinsEarned = 0;
            quarterlyStats.trainingsCompleted = 0;
            quarterlyStats.punctualDays = 0;
            quarterlyStats.feedbackGiven = 0;
          }

          switch (eventType) {
            case 'training_completed':
              quarterlyStats.trainingsCompleted += value;
              break;
            case 'coins_earned':
              quarterlyStats.coinsEarned += value;
              break;
            case 'punctual_checkin':
              quarterlyStats.punctualDays += value;
              // Update daily streak
              const today = new Date().toISOString().split('T')[0];
              const lastCheckin = userProgress.dailyStreak.lastCheckin;
              const yesterday = new Date(Date.now() - 86400000).toISOString().split('T')[0];

              if (lastCheckin === yesterday) {
                userProgress.dailyStreak.current += 1;
              } else if (lastCheckin !== today) {
                userProgress.dailyStreak.current = 1;
              }
              userProgress.dailyStreak.longest = Math.max(
                userProgress.dailyStreak.longest,
                userProgress.dailyStreak.current
              );
              userProgress.dailyStreak.lastCheckin = today;
              break;
            case 'feedback_given':
              quarterlyStats.feedbackGiven += value;
              break;
          }

          const updatedProgress = {
            ...userProgress,
            quarterlyStats
          };

          return {
            userProgress: {
              ...state.userProgress,
              [userId]: updatedProgress
            }
          };
        });

        // Check for new achievements
        get().checkAchievements(userId);
      },

      checkAchievements: (_userId: string) => {
        const userAchievements = get().getUserAchievements(userId);
        const unlockedIds = userAchievements.map(ua => ua.achievementId);
        const lockedAchievements = get().achievements.filter(a => !unlockedIds.includes(a.id));
        const userProgress = get().userProgress[userId];
        const newUnlocks: UserAchievement[] = [];

        if (!userProgress) return newUnlocks;

        lockedAchievements.forEach(achievement => {
          const isUnlocked = achievement.conditions.every(condition => {
            return get().checkCondition(userId, condition, userProgress);
          });

          if (isUnlocked) {
            const userAchievement = {
              id: `${userId}-${achievement.id}`,
              userId,
              achievementId: achievement.id,
              progress: 100,
              unlockedAt: new Date().toISOString(),
              notified: false,
              name: achievement.name,
              description: achievement.description,
              icon: achievement.icon,
              rarity: achievement.rarity
            };

            newUnlocks.push(userAchievement);
            get().unlockAchievement(userId, achievement.id);

            // Add notification
            const notificationsStore = useNotificationsStore.getState();
            notificationsStore.addNotification({
              userId,
              type: 'general',
              title: 'Achievement freigeschaltet!',
              message: `Du hast "${achievement.name}" erreicht!`,
              isRead: false,
              relatedId: achievement.id
            });

            // Process rewards
            if (achievement.rewards) {
              const avatarStore = useAvatarStore.getState();
              achievement.rewards.forEach(reward => {
                switch (reward.type) {
                  case 'xp':
                    avatarStore.addXP(
                      userId, 
                      null, 
                      Number(reward.value), 
                      `Achievement: ${achievement.name}`
                    );
                    break;
                  case 'skill_xp':
                    if (reward.skillId) {
                      avatarStore.addXP(
                        userId,
                        reward.skillId,
                        Number(reward.value),
                        `Achievement: ${achievement.name}`
                      );
                    }
                    break;
                  case 'coins':
                    // Add coins logic here when coins store is available
                    break;
                }
              });
            }
          }
        });

        return newUnlocks;
      },


      // Helper functions for getting total values (these would integrate with other stores)
      getTotalTrainingsCompleted: (_userId: string) => {
        // This would integrate with training store
        return 0;
      },

      getTotalPunctualDays: (_userId: string) => {
        // This would integrate with time records store  
        return 0;
      },

      getTotalCoinsEarned: (_userId: string) => {
        // This would integrate with coins store
        return 0;
      },

      getTotalFeedbackGiven: (_userId: string) => {
        // This would integrate with feedback/training store
        return 0;
      },

      unlockAchievement: (_userId: string, _achievementId: string) => {
        const avatarStore = useAvatarStore.getState();
        let userAvatar = avatarStore.getUserAvatar(userId);

        if (!userAvatar) {
          userAvatar = avatarStore.createUserAvatar(userId);
        }

        const newAchievement = {
          achievementId,
          unlockedAt: new Date().toISOString(),
          isNew: true
        };

        const updatedAchievements = [...userAvatar.achievements, newAchievement];

        // Update avatar store
        avatarStore.updateSkill(userId, 'achievements', updatedAchievements as any);
      },

      addAchievement: (_achievementData: Omit<Achievement, 'id'>) => {
        const newAchievement: Achievement = {
          ...achievementData,
          id: `custom-${Date.now()}`
        };

        set(state => ({
          achievements: [...state.achievements, newAchievement]
        }));
      },

      updateAchievement: (_achievementId: string, _updates: Partial<Achievement>) => {
        set(state => ({
          achievements: state.achievements.map(achievement =>
            achievement.id === achievementId ? { ...achievement, ...updates } : achievement
          )
        }));
      },

      deleteAchievement: (_achievementId: string) => {
        set(state => ({
          achievements: state.achievements.filter(achievement => achievement.id !== achievementId)
        }));
      },

      getProgressStats: (_userId: string) => {
        const allAchievements = get().achievements;
        const userAchievements = get().getUserAchievements(userId);

        const totalAchievements = allAchievements.filter(a => a.isActive && !a.isHidden).length;
        const unlockedAchievements = userAchievements.length;
        const completionRate = totalAchievements > 0 ? (unlockedAchievements / totalAchievements) * 100 : 0;

        const recentUnlocks = userAchievements
          .sort((a, b) => new Date(b.unlockedAt).getTime() - new Date(a.unlockedAt).getTime())
          .slice(0, 3);

        return {
          totalAchievements,
          unlockedAchievements,
          completionRate,
          recentUnlocks
        };
      },

      createAchievement: (achievement: Achievement) => {
        set(state => ({
          achievements: [...state.achievements, achievement]
        }));
      },

      toggleAchievementActive: (_achievementId: string) => {
        set(state => ({
          achievements: state.achievements.map(achievement =>
            achievement.id === achievementId 
              ? { ...achievement, isActive: !achievement.isActive }
              : achievement
          )
        }));
      },

      getAchievementStats: (_achievementId: string) => {
        const avatarStore = useAvatarStore.getState();
        const allUserAvatars = avatarStore.getAllUserAvatars();

        let unlockedCount = 0;
        allUserAvatars.forEach(avatar => {
          if (avatar.achievements.some(a => a.achievementId === achievementId)) {
            unlockedCount++;
          }
        });

        return { unlockedCount };
      },

      checkCondition: (_userId: string, condition: AchievementCondition, userProgress: ProgressTracker) => {
        let currentValue = 0;

        switch (condition.type) {
          case 'xp_earned':
            const avatarStore = useAvatarStore.getState();
            const userAvatar = avatarStore.getUserAvatar(userId);
            currentValue = userAvatar?.totalXP || 0;
            break;
          case 'training_completed':
          case 'trainings_completed':
            currentValue = condition.timeframe === 'quarterly' 
              ? userProgress.quarterlyStats.trainingsCompleted
              : get().getTotalTrainingsCompleted(userId);
            break;
          case 'days_punctual':
          case 'punctual_checkins':
            currentValue = condition.timeframe === 'quarterly'
              ? userProgress.quarterlyStats.punctualDays
              : get().getTotalPunctualDays(userId);
            break;
          case 'coins_earned':
            currentValue = condition.timeframe === 'quarterly'
              ? userProgress.quarterlyStats.coinsEarned
              : get().getTotalCoinsEarned(userId);
            break;
          case 'feedback_given':
            currentValue = condition.timeframe === 'quarterly'
              ? userProgress.quarterlyStats.feedbackGiven
              : get().getTotalFeedbackGiven(userId);
            break;
          case 'level_reached':
            const avatar = useAvatarStore.getState().getUserAvatar(userId);
            currentValue = avatar?.level || 1;
            break;
          case 'consecutive_days':
            currentValue = userProgress.dailyStreak.current;
            break;
        }

        // Check condition
        switch (condition.operator) {
          case 'equals':
          case 'eq':
            return currentValue === condition.target;
          case 'gte':
            return currentValue >= condition.target;
          case 'gt':
            return currentValue > condition.target;
          case 'lte':
            return currentValue <= condition.target;
          case 'lt':
            return currentValue < condition.target;
          default:
            return false;
        }
      }
    }),
    {
      name: 'achievements-storage',
      storage: createJSONStorage(() => localStorage),
      partialize: (state) => ({
        achievements: state.achievements,
        userProgress: state.userProgress
      })
    }
  )
);

import { create } from 'zustand';
import { persist, createJSONStorage } from 'zustand/middleware';
import { 
  UserAvatar, 
  Skill, 
  Level, 
  XPEvent, 
  LevelUpEvent,
  DEFAULT_SKILLS,
  SKILL_IDS,
  calculateXPProgress,
  calculateLevelFromXP
} from '../types/avatar';

interface AvatarState {
  // User avatar data
  userAvatars: Record<string, UserAvatar>;

  // System configuration
  levels: Level[];
  xpEvents: XPEvent[];
  levelUpEvents: LevelUpEvent[];

  // Actions
  getUserAvatar: (_userId: string) => UserAvatar | null;
  createUserAvatar: (_userId: string) => UserAvatar;
  addXP: (_userId: string, _skillId: string | null, _xpAmount: number, _description: string, _metadata?: Record<string, any>) => void;
  updateSkill: (_userId: string, _skillId: string, _updates: Partial<Skill>) => void;

  // Level management
  getLevels: () => Level[];
  getLevel: (_levelNumber: number) => Level | null;
  addLevel: (level: Omit<Level, 'id'>) => void;
  updateLevel: (_levelId: string, _updates: Partial<Level>) => void;
  deleteLevel: (_levelId: string) => void;

  // XP and level calculations
  calculateUserLevel: (_userId: string) => number;
  checkLevelUp: (_userId: string) => LevelUpEvent[];

  // Statistics
  getXPEvents: (_userId: string, _limit?: number) => XPEvent[];
  getLevelUpEvents: (_userId: string, _limit?: number) => LevelUpEvent[];
  getUserStats: (_userId: string) => {
    totalXP: number;
    level: number;
    skillLevels: Record<string, number>;
    recentEvents: XPEvent[];
  };

  // Additional methods
  getAllUserAvatars: () => UserAvatar[];
  getUserSkill: (_userId: string, _skillId: string) => Skill | undefined;
  updateTitle: (_userId: string, _title: string) => void;
  updateAvatar: (_userId: string, _avatarConfig: Partial<UserAvatar>) => void;
}

// Mock default levels
const mockLevels: Level[] = [
  { id: '1', levelNumber: 1, title: 'Neuling', requiredXP: 0, icon: 'üå±', color: '#10B981' },
  { id: '2', levelNumber: 2, title: 'Anf√§nger', requiredXP: 100, icon: 'üî∞', color: '#3B82F6' },
  { id: '3', levelNumber: 3, title: 'Lernender', requiredXP: 250, icon: 'üìö', color: '#6366F1' },
  { id: '4', levelNumber: 4, title: 'Fortgeschrittener', requiredXP: 450, icon: '‚≠ê', color: '#8B5CF6' },
  { id: '5', levelNumber: 5, title: 'Explorer', requiredXP: 700, icon: 'üß≠', color: '#F59E0B' },
  { id: '6', levelNumber: 6, title: 'Spezialist', requiredXP: 1000, icon: 'üéØ', color: '#EF4444' },
  { id: '7', levelNumber: 7, title: 'Experte', requiredXP: 1350, icon: 'üíé', color: '#06B6D4' },
  { id: '8', levelNumber: 8, title: 'Meister', requiredXP: 1750, icon: 'üëë', color: '#8B5CF6' },
  { id: '9', levelNumber: 9, title: 'Profi', requiredXP: 2200, icon: 'üèÜ', color: '#F59E0B' },
  { id: '10', levelNumber: 10, title: 'Sensei', requiredXP: 2700, icon: 'ü•á', color: '#EF4444' }
];

export const useAvatarStore = create<AvatarState>()(
  persist(
    (set, get) => (_{
      userAvatars: {}, levels: mockLevels, xpEvents: [], levelUpEvents: [], _getUserAvatar: (userId) => {
        const avatars = get().userAvatars;
        return avatars[userId] || null;
      },

      createUserAvatar: (_userId: string) => {
        const now = new Date().toISOString();

        // Create default skills with 0 XP
        const skills: Skill[] = DEFAULT_SKILLS.map(skillTemplate => ({
          ...skillTemplate,
          currentXP: 0,
          level: 1,
          totalXP: 0
        }));

        const newAvatar: UserAvatar = {
          userId,
          level: 1,
          totalXP: 0,
          currentLevelXP: 0,
          nextLevelXP: 100,
          skills,
          achievements: [],
          lastActiveAt: now,
          createdAt: now,
          updatedAt: now
        };

        set(state => ({
          userAvatars: {
            ...state.userAvatars,
            [userId]: newAvatar
          }
        }));

        return newAvatar;
      },

      addXP: (_userId: string, _skillId: string | null | undefined, _xpAmount: number, _description: string, metadata = {}) => {
        const now = new Date().toISOString();
        let userAvatar = get().getUserAvatar(userId);

        // Create avatar if it doesn't exist
        if (!userAvatar) {
          userAvatar = get().createUserAvatar(userId);
        }

        // Create XP event
        const xpEvent: XPEvent = {
          id: `${userId}-${Date.now()}`,
          userId,
          type: metadata.type || 'manual',
          skillId: skillId || undefined,
          xpAmount,
          description,
          metadata,
          createdAt: now
        };

        set(state => {
          const updatedAvatar = { ...state.userAvatars[userId] };

          // Add to total XP
          updatedAvatar.totalXP += xpAmount;

          // Update specific skill if provided
          if (skillId) {
            updatedAvatar.skills = updatedAvatar.skills.map(skill => {
              if (skill.id === skillId) {
                const newTotalXP = skill.totalXP + xpAmount;
                const skillProgress = calculateXPProgress(newTotalXP);

                return {
                  ...skill,
                  currentXP: skillProgress.currentLevelXP,
                  totalXP: newTotalXP,
                  level: skillProgress.level
                };
              }
              return skill;
            });
          }

          // Update overall level progress
          const overallProgress = calculateXPProgress(updatedAvatar.totalXP);
          updatedAvatar.level = overallProgress.level;
          updatedAvatar.currentLevelXP = overallProgress.currentLevelXP;
          updatedAvatar.nextLevelXP = overallProgress.nextLevelXP;
          updatedAvatar.lastActiveAt = now;
          updatedAvatar.updatedAt = now;

          return {
            userAvatars: {
              ...state.userAvatars,
              [userId]: updatedAvatar
            },
            xpEvents: [xpEvent, ...state.xpEvents]
          };
        });

        // Check for level ups
        get().checkLevelUp(userId);
      },

      updateSkill: (_userId: string, _skillId: string, _updates: Partial<Skill>) => {
        set(state => {
          const userAvatar = state.userAvatars[userId];
          if (!userAvatar) return state;

          const updatedSkills = userAvatar.skills.map(skill =>
            skill.id === skillId ? { ...skill, ...updates } : skill
          );

          return {
            userAvatars: {
              ...state.userAvatars,
              [userId]: {
                ...userAvatar,
                skills: updatedSkills,
                updatedAt: new Date().toISOString()
              }
            }
          };
        });
      },

      getLevels: () => get().levels,

      getLevel: (_levelNumber: number) => {
        return get().levels.find(level => level.levelNumber === levelNumber) || null;
      },

      addLevel: (_levelData: Omit<Level, 'id'>) => {
        const newLevel: Level = {
          ...levelData,
          id: `level-${Date.now()}`
        };

        set(_state => ({
          levels: [...state.levels, newLevel].sort((a, b) => a.levelNumber - b.levelNumber)
        }));
      },

      updateLevel: (_levelId: string, _updates: Partial<Level>) => {
        set(state => ({
          levels: state.levels.map(level =>
            level.id === levelId ? { ...level, ...updates } : level
          )
        }));
      },

      deleteLevel: (_levelId: string) => {
        set(state => ({
          levels: state.levels.filter(level => level.id !== levelId)
        }));
      },

      calculateUserLevel: (_userId: string) => {
        const userAvatar = get().getUserAvatar(userId);
        if (!userAvatar) return 1;

        return calculateLevelFromXP(userAvatar.totalXP);
      },

      checkLevelUp: (_userId: string) => {
        const userAvatar = get().getUserAvatar(userId);
        if (!userAvatar) return [];

        const levelUpEvents: LevelUpEvent[] = [];
        const now = new Date().toISOString();

        // Check overall level up
        const newOverallLevel = calculateLevelFromXP(userAvatar.totalXP);
        if (newOverallLevel > userAvatar.level) {
          const levelUpEvent: LevelUpEvent = {
            userId,
            oldLevel: userAvatar.level,
            newLevel: newOverallLevel,
            rewards: [], // Add level rewards logic here
            timestamp: now
          };
          levelUpEvents.push(levelUpEvent);
        }

        // Check skill level ups
        userAvatar.skills.forEach(skill => {
          const newSkillLevel = calculateLevelFromXP(skill.totalXP);
          if (newSkillLevel > skill.level) {
            const skillLevelUpEvent: LevelUpEvent = {
              userId,
              oldLevel: skill.level,
              newLevel: newSkillLevel,
              skillId: skill.id,
              rewards: [], // Add skill level rewards logic here
              timestamp: now
            };
            levelUpEvents.push(skillLevelUpEvent);
          }
        });

        if (levelUpEvents.length > 0) {
          set(state => ({
            levelUpEvents: [...levelUpEvents, ...state.levelUpEvents]
          }));
        }

        return levelUpEvents;
      },

      getXPEvents: (_userId: string, limit = 10) => {
        return get().xpEvents
          .filter(event => event.userId === userId)
          .slice(0, limit);
      },

      getLevelUpEvents: (_userId: string, limit = 5) => {
        return get().levelUpEvents
          .filter(event => event.userId === userId)
          .slice(0, limit);
      },

      getUserStats: (_userId: string) => {
        const userAvatar = get().getUserAvatar(userId);
        const xpEvents = get().getXPEvents(userId, 5);

        if (!userAvatar) {
          return {
            totalXP: 0,
            level: 1,
            skillLevels: {},
            recentEvents: []
          };
        }

        const skillLevels = userAvatar.skills.reduce((acc, skill) => {
          acc[skill.id] = skill.level;
          return acc;
        }, {} as Record<string, number>);

        return {
          totalXP: userAvatar.totalXP,
          level: userAvatar.level,
          skillLevels,
          recentEvents: xpEvents
        };
      },

      getAllUserAvatars: () => {
        return Object.values(get().userAvatars);
      },

      getUserSkill: (_userId: string, _skillId: string) => {
        const userAvatar = get().getUserAvatar(userId);
        if (!userAvatar) return undefined;
        return userAvatar.skills.find(skill => skill.id === skillId);
      },

      updateTitle: (_userId: string, _title: string) => {
        set(state => {
          const userAvatar = state.userAvatars[userId];
          if (!userAvatar) return state;

          return {
            userAvatars: {
              ...state.userAvatars,
              [userId]: {
                ...userAvatar,
                title,
                updatedAt: new Date().toISOString()
              }
            }
          };
        });
      },

      updateAvatar: (_userId: string, _avatarConfig: Partial<UserAvatar>) => {
        set(state => {
          const userAvatar = state.userAvatars[userId];
          if (!userAvatar) return state;

          return {
            userAvatars: {
              ...state.userAvatars,
              [userId]: {
                ...userAvatar,
                ...avatarConfig,
                updatedAt: new Date().toISOString()
              }
            }
          };
        });
      }
    }),
    {
      name: 'avatar-storage',
      storage: createJSONStorage(() => localStorage),
      partialize: (state) => ({
        userAvatars: state.userAvatars,
        levels: state.levels,
        xpEvents: state.xpEvents.slice(0, 100), // Keep only recent events
        levelUpEvents: state.levelUpEvents.slice(0, 50)
      })
    }
  )
);

import { create } from 'zustand';
import { persist, createJSONStorage } from 'zustand/middleware';
import { 
  XPEventData, 
  GamificationConfig, 
  DEFAULT_GAMIFICATION_CONFIG,
  NotificationEvent
} from '../types/gamification';
import { SKILL_IDS } from '../types/avatar';
import { useAvatarStore } from './avatar';
import { useAchievementsStore } from './achievements';

interface GamificationState {
  // Configuration
  config: GamificationConfig;

  // Event tracking
  notifications: NotificationEvent[];

  // Main actions
  awardXP: (_eventData: XPEventData) => void;
  awardCoins: (_userId: string, _amount: number, _reason: string) => void;

  // Event handlers for different activities
  onTrainingCompleted: (_userId: string, _trainingId: string, _passed: boolean) => void;
  onPunctualCheckin: (_userId: string, _streakDay?: number) => void;
  onCoinsEarned: (_userId: string, _amount: number, _reason: string) => void;
  onFeedbackGiven: (_userId: string, _feedbackId: string) => void;
  onDailyLogin: (_userId: string) => void;

  // Configuration management
  updateConfig: (_updates: Partial<GamificationConfig>) => void;

  // Notifications
  getNotifications: (_userId: string) => NotificationEvent[];
  markNotificationAsRead: (_notificationId: string) => void;
  clearNotifications: (_userId: string) => void;

  // Statistics and leaderboards
  getLeaderboard: (_skillId?: string, _limit?: number) => Array<{
    userId: string;
    userName: string;
    xp: number;
    level: number;
  }>;

  // Integration helpers
  initializeUserGamification: (_userId: string) => void;
  getUserGamificationSummary: (_userId: string) => {
    level: number;
    totalXP: number;
    achievements: number;
    weeklyXP: number;
    rank: number;
  };

  // Helper methods
  getXPDescription: (_type: string, metadata: Record<string, any>) => string;
  getDefaultSkillForEventType: (_type: string) => string | null;
}

export const useGamificationStore = create<GamificationState>()(
  persist(
    (set, get) => (_{
      config: DEFAULT_GAMIFICATION_CONFIG, notifications: [], _awardXP: (eventData) => {
        const { type, userId, skillIds, xpAmount, metadata = {} } = eventData;
        const config = get().config;

        // Calculate XP amount if not provided
        let finalXPAmount = xpAmount;
        if (!finalXPAmount) {
          switch (type) {
            case 'training_completed':
              finalXPAmount = config.xpRates.trainingCompleted;
              break;
            case 'punctual_checkin':
              finalXPAmount = config.xpRates.punctualCheckin;
              break;
            case 'coins_earned':
              finalXPAmount = Math.floor((metadata.coinAmount || 0) * config.xpRates.coinsEarned);
              break;
            case 'feedback_given':
              finalXPAmount = config.xpRates.feedbackGiven;
              break;
            case 'daily_login':
              finalXPAmount = config.xpRates.dailyLogin;
              break;
            default:
              finalXPAmount = 0;
          }
        }

        if (!finalXPAmount || finalXPAmount <= 0) return;

        const avatarStore = useAvatarStore.getState();
        const achievementsStore = useAchievementsStore.getState();

        // Award XP to specific skills or general
        if (skillIds && skillIds.length > 0) {
          skillIds.forEach(skillId => {
            avatarStore.addXP(
              userId,
              skillId,
              finalXPAmount!,
              get().getXPDescription(type, metadata),
              { type, ...metadata }
            );
          });
        } else {
          // Award to default skill based on type
          const defaultSkillId = get().getDefaultSkillForEventType(type);
          avatarStore.addXP(
            userId,
            defaultSkillId,
            finalXPAmount!,
            get().getXPDescription(type, metadata),
            { type, ...metadata }
          );
        }

        // Update achievement progress
        achievementsStore.updateProgress(userId, type, metadata.coinAmount || 1, metadata);

        // Create notification
        set(state => {
          const notification: NotificationEvent = {
            id: `xp-${userId}-${Date.now()}`,
            userId,
            type: skillIds ? 'skill_level_up' : 'level_up',
            title: 'XP erhalten!',
            message: `+${finalXPAmount!} XP f√ºr ${get().getXPDescription(type, metadata)}`,
            data: {
              xpGained: finalXPAmount!,
              skillId: skillIds?.[0]
            },
            isRead: false,
            createdAt: new Date().toISOString()
          };

          return {
            notifications: [notification, ...state.notifications.slice(0, 99)]
          };
        });
      },

      getXPDescription: (_type: string, metadata: Record<string, any>) => {
        switch (type) {
          case 'training_completed':
            return `Schulung abgeschlossen`;
          case 'punctual_checkin':
            return metadata.streakDay > 1 
              ? `P√ºnktlich (${metadata.streakDay} Tage Serie)`
              : 'P√ºnktlich gestempelt';
          case 'coins_earned':
            return `${metadata.coinAmount} Coins verdient: ${metadata.reason}`;
          case 'feedback_given':
            return 'Feedback gegeben';
          case 'daily_login':
            return 'T√§glicher Login';
          case 'manual':
            return metadata.reason || 'Manuell vergeben';
          default:
            return 'XP erhalten';
        }
      },

      getDefaultSkillForEventType: (_type: string) => {
        switch (type) {
          case 'training_completed':
          case 'training_passed':
            return SKILL_IDS.KNOWLEDGE;
          case 'punctual_checkin':
          case 'feedback_given':
          case 'daily_login':
            return SKILL_IDS.LOYALTY;
          case 'coins_earned':
            return SKILL_IDS.HUSTLE;
          default:
            return null;
        }
      },

      awardCoins: (_userId: string, _amount: number, _reason: string) => {
        // This would integrate with the existing coins store
        // For now, we just track it for XP purposes
        get().awardXP({
          type: 'coins_earned',
          userId,
          metadata: { coinAmount: amount, reason }
        });
      },

      onTrainingCompleted: (_userId: string, _trainingId: string, _passed: boolean) => {
        if (!passed) return;

        get().awardXP({
          type: 'training_completed',
          userId,
          skillIds: [SKILL_IDS.KNOWLEDGE],
          metadata: { trainingId, passed }
        });
      },

      onPunctualCheckin: (_userId: string, streakDay = 1) => {
        get().awardXP({
          type: 'punctual_checkin',
          userId,
          skillIds: [SKILL_IDS.LOYALTY],
          metadata: { streakDay }
        });
      },

      onCoinsEarned: (_userId: string, _amount: number, _reason: string) => {
        get().awardXP({
          type: 'coins_earned',
          userId,
          skillIds: [SKILL_IDS.HUSTLE],
          metadata: { coinAmount: amount, reason }
        });
      },

      onFeedbackGiven: (_userId: string, _feedbackId: string) => {
        get().awardXP({
          type: 'feedback_given',
          userId,
          skillIds: [SKILL_IDS.LOYALTY],
          metadata: { feedbackId }
        });
      },

      onDailyLogin: (_userId: string) => {
        const today = new Date().toISOString().split('T')[0];
        const lastLogin = localStorage.getItem(`lastLogin-${userId}`);

        if (lastLogin !== today) {
          localStorage.setItem(`lastLogin-${userId}`, today);
          get().awardXP({
            type: 'daily_login',
            userId,
            metadata: { date: today }
          });
        }
      },


      updateConfig: (_updates: Partial<GamificationConfig>) => {
        set(state => ({
          config: { ...state.config, ...updates }
        }));
      },

      getNotifications: (_userId: string) => {
        return get().notifications.filter(n => n.userId === userId);
      },

      markNotificationAsRead: (_notificationId: string) => {
        set(state => ({
          notifications: state.notifications.map(n =>
            n.id === notificationId ? { ...n, isRead: true } : n
          )
        }));
      },

      clearNotifications: (_userId: string) => {
        set(state => ({
          notifications: state.notifications.filter(n => n.userId !== userId)
        }));
      },

      getLeaderboard: (_skillId?: string, limit = 10) => {
        const avatarStore = useAvatarStore.getState();
        const allAvatars = Object.values(avatarStore.userAvatars);

        let leaderboard = allAvatars.map(avatar => {
          let xp = avatar.totalXP;
          let level = avatar.level;

          if (skillId) {
            const skill = avatar.skills.find(s => s.id === skillId);
            xp = skill?.totalXP || 0;
            level = skill?.level || 1;
          }

          return {
            userId: avatar.userId,
            userName: `User ${avatar.userId}`, // This would come from user store
            xp,
            level
          };
        });

        return leaderboard
          .sort((a, b) => b.xp - a.xp)
          .slice(0, limit);
      },

      initializeUserGamification: (_userId: string) => {
        const avatarStore = useAvatarStore.getState();
        const existingAvatar = avatarStore.getUserAvatar(userId);

        if (!existingAvatar) {
          avatarStore.createUserAvatar(userId);
        }

        // Award welcome XP
        get().awardXP({
          type: 'manual',
          userId,
          xpAmount: 25,
          metadata: { reason: 'Willkommen bei HRthis!' }
        });
      },

      getUserGamificationSummary: (_userId: string) => {
        const avatarStore = useAvatarStore.getState();
        const achievementsStore = useAchievementsStore.getState();

        const userAvatar = avatarStore.getUserAvatar(userId);
        const userAchievements = achievementsStore.getUserAchievements(userId);
        const leaderboard = get().getLeaderboard();

        if (!userAvatar) {
          return {
            level: 1,
            totalXP: 0,
            achievements: 0,
            weeklyXP: 0,
            rank: leaderboard.length + 1
          };
        }

        // Calculate weekly XP (this would need proper date tracking)
        const weeklyXP = avatarStore.getXPEvents(userId, 50)
          .filter(event => {
            const eventDate = new Date(event.createdAt);
            const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
            return eventDate >= weekAgo;
          })
          .reduce((sum, event) => sum + event.xpAmount, 0);

        const rank = leaderboard.findIndex(entry => entry.userId === userId) + 1;

        return {
          level: userAvatar.level,
          totalXP: userAvatar.totalXP,
          achievements: userAchievements.length,
          weeklyXP,
          rank: rank || leaderboard.length + 1
        };
      }
    }),
    {
      name: 'gamification-storage',
      storage: createJSONStorage(() => localStorage),
      partialize: (state) => ({
        config: state.config,
        notifications: state.notifications.slice(0, 50) // Keep recent notifications
      })
    }
  )
);

import { create } from 'zustand';
import { persist, createJSONStorage } from 'zustand/middleware';
import { VacationReminder, ReminderSettings, ReminderNotification } from '../types/reminders';
import { LeaveRequest } from '../types';

interface RemindersState {
  reminders: VacationReminder[];
  reminderSettings: ReminderSettings[];
  notifications: ReminderNotification[];
  isLoading: boolean;

  // Reminder management
  createReminderForVacation: (leaveRequest: LeaveRequest, managerId: string, customDays?: number[]) => Promise<void>;
  createManualReminder: (leaveRequestId: string, reminderDate: string, message: string, managerId: string) => Promise<void>;
  deleteReminder: (reminderId: string) => void;
  updateReminder: (reminderId: string, updates: Partial<VacationReminder>) => void;

  // Settings management
  getReminderSettings: (managerId: string) => ReminderSettings | null;
  updateReminderSettings: (managerId: string, settings: Partial<ReminderSettings>) => Promise<void>;

  // Notifications
  getUpcomingNotifications: (managerId: string) => ReminderNotification[];
  markNotificationAsRead: (notificationId: string) => void;
  generateNotificationsForToday: () => ReminderNotification[];

  // Calendar integration
  getRemindersForLeave: (leaveRequestId: string) => VacationReminder[];
  getUpcomingVacationAlerts: (managerId: string, days?: number) => ReminderNotification[];
}

