  useAuthStore.setState({
    user,
    isAuthenticated,
    organization: mockOrganization,
    isLoading: false,
  });

  window.history.pushState({}, 'Test page', initialRoute);

  return render(ui, _{
    wrapper: ({ children }) => <AllTheProviders initialRoute={initialRoute}>{children}</AllTheProviders>,
    ...renderOptions,
  });
};

